INSERT INTO `adminHUS`.`new` (`id`,`id_category`,`title`,`description`,`content`,`start_date`,`end_date`,`created_at`,`update_at`,`deleted_at`,`avatar`,`location`) VALUES (90, 8, 'Kêu gọi nộp đề xuất dự án nghiên cứu trong khuôn khổ Chương trình LASER PULSE', 'Chương trình LASER PULSE (Long-term Assistance and Services for Research: Partners for University-Led Solutions Engine - Hỗ trợ dài hạn và các dịch vụ dành cho nghiên cứu: Hợp tác thúc đẩy cơ chế giải pháp do các đại học dẫn dắt) là chương trình được triển khai trong 5 năm do một nhóm các Đại học và Tổ chức thực hiện, gồm có ĐH Purdue, ĐH Notre Dame, ĐH Indiana (Hoa Kỳ), ĐH Makerere (Uganda) và Tổ chức Catholic Relief Services. Chương trình được triển khai với sự tài trợ của Cơ quan Phát triển Quốc tế của Hoa Kỳ - USAID.', '<p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Chương trình LASER-PULSE mở kêu gọi nộp đề xuất các dự án cho các đối tác Việt Nam trong các lĩnh vực: ô nhiễm không khí, ô nhiễm nước và tính cạnh tranh của các doanh nghiệp vừa và nhỏ. Chương trình dự kiến sẽ tài trợ khoảng một triệu đô la Mỹ cho 4 - 6 đề xuất dự án nghiên cứu và ứng dụng triển khai từ 12 đến 24 tháng.  </p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trân trọng kính mời các nhà khoa học của ĐHQGHN có quan tâm đăng ký tham gia nộp đề xuất dự án.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Thời hạn nộp Thư quan tâm (Letter of Interest): 21 tháng 8 năm 2020</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Thời hạn nộp đề xuất: 10 tháng 9 năm 2020</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Thông tin chi tiết về kêu gọi nộp đề xuất, các hướng dẫn và mẫu đăng ký xin xem tại link: <a href=\"https://stemedhub.org/groups/laserpulse/funding/rfa-vietnam\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\">https://stemedhub.org/groups/laserpulse/funding/rfa-vietnam</a>. Các câu hỏi liên quan xin gửi về: Pamela McClure theo địa chỉ: <a target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\">pmcclure@purdue.edu</a>.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Để hỗ trợ về mặt thông tin, xin liên hệ Ban Hợp tác và Phát triển, ĐHQGHN tại địa chỉ: <a href=\"mailto:cooperation@vnu.edu.vn\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\">cooperation@vnu.edu.vn</a></p>', '', '', '2020-08-07 02:00:57', '2020-08-25 02:34:09', NULL, '/php-intership-team/admin/SourceFile/upload/dhqg.jpg', ''),(91, 7, 'Hội nghị Tổng kết Hợp phần 2 “Tăng cường năng lực phòng chống thiên tai” thuộc Dự án Khắc phục khẩn cấp hậu quả thiên tai tại một số tỉnh miền Trung (ENDRP)', 'Sáng ngày 31/07/2020 tại Hà Nội, Tổng cục Phòng, chống thiên tai và Ngân hàng Thế giới (World Bank) đã phối hợp tổ chức Hội nghị Tổng kết Hợp phần 2 “Tăng cường năng lực phòng chống thiên tai” thuộc Dự án Khắc phục khẩn cấp hậu quả thiên tai tại một số tỉnh miền Trung (ENDRP) để tổng kết, đánh giá các hoạt động và kết quả đạt được của từng nội dung Hợp phần 2 cũng như cơ hội để các địa phương, Bộ ngành, các nhà khoa học chia sẻ các ý kiến đánh giá, góp ý, cũng như triển vọng ứng dụng các kết quả của dự án.', '<div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/cefd-tong-ket-hop-phan-2-3.png\" rel=\"newsdetailslidephoto\" title=\"Hội nghị Tổng kết Hợp phần 2 “Tăng cường năng lực phòng chống thiên tai” thuộc Dự án Khắc phục khẩn cấp hậu quả thiên tai tại một số tỉnh miền Trung (ENDRP)\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Hội nghị Tổng kết Hợp phần 2 “Tăng cường năng lực phòng chống thiên tai” thuộc Dự án Khắc phục khẩn cấp hậu quả thiên tai tại một số tỉnh miền Trung (ENDRP)\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/cefd-tong-ket-hop-phan-2-3.png\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.662px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Hội nghị do TS. Lê Minh Nhật - Phó Cục trưởng Cục Ứng phó và Khắc phục hậu quả thiên tai và ông Nguyễn Huy Dũng - Chuyên gia cao cấp Ngân hàng Thế giới (World Bank) chủ trì. Tới tham dự Hội nghị còn có sự hiện diện của đại diện Tổng cục Phòng chống thiên tai; đại diện đơn vị tư vấn JICA, CTI; đại biểu Viện khoa học tổ chức nhà nước, Trung tâm tư vấn Phát triển Kinh tế, Viện Nghiên cứu Thiên tai và Môi trường, Trung tâm Động lực học Thủy khí Môi trường, Trường Đại học Thủy lợi và các đơn vị phối hợp tại địa phương. Trong những đơn vị tham gia, phải kể đến Trung tâm Động lực học Thủy khí Môi trường (CEFD), Trường Đại học Khoa học Tự nhiên – Đại học Quốc gia Hà Nội. CEFD là một trong những thành viên quan trọng tham gia vào các gói thầu và tạo nên sự thành công cho các sản phẩm hữu ích trong hợp phần này. CEFD là thành viên đứng đầu trong Liên danh tư vấn Trung tâm Động lực học Thủy khí Môi trường – Công ty TNHH Tư vấn Trường Đại học Thủy lợi – Công ty TNHH Tư vấn Tài nguyên nước và Môi trường Minh Long, tham gia gói thầu <em style=\"margin: 0px; padding: 0px;\">“Xây dựng kế hoạch phòng chống thiên tai cấp tỉnh gắn với quản lý lũ tổng hợp một số lưu vực sông khu vực Nam Trung Bộ và Tây Nguyên”</em> và đồng thời là thành viên liên danh cùng Trung tâm Tư vấn Phát triển Kinh tế (EPCC) thực hiện gói thầu <em style=\"margin: 0px; padding: 0px;\">“Xây dựng các khuyến nghị về quy trình thủ tục rút gọn để huy động và triển khai nguồn lực hỗ trợ khẩn cấp cho khắc phục hậu quả, phục hồi và tái thiết sau thiên tai”.</em></p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/cefd-tong-ket-hop-phan-2-1.png\" rel=\"newsdetailslidephoto\" title=\"Hội nghị Tổng kết Hợp phần 2 “Tăng cường năng lực phòng chống thiên tai” thuộc Dự án Khắc phục khẩn cấp hậu quả thiên tai tại một số tỉnh miền Trung (ENDRP)\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Hội nghị Tổng kết Hợp phần 2 “Tăng cường năng lực phòng chống thiên tai” thuộc Dự án Khắc phục khẩn cấp hậu quả thiên tai tại một số tỉnh miền Trung (ENDRP)\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/cefd-tong-ket-hop-phan-2-1.png\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.662px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">PGS.TS. Trần Ngọc Anh - Giám đốc CEFD, Trường ĐHKHTN, Trưởng nhóm tư vấn trình bày kết quả thực hiện hoạt động “Xây dựng kế hoạch phòng chống thiên tai cấp tỉnh gắn với quản lý lũ tổng hợp một số lưu vực sông khu vực Nam Trung Bộ và Tây Nguyên” tại hội nghị.</em></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Tại hội thảo, TS. Lê Minh Nhật đã gửi lời cảm ơn tới PGS.TS. Trần Ngọc Anh - Giám đốc CEFD cùng đội ngũ chuyên gia được huy động lớn nhất từ trước tới nay thực hiện dự án cho 3 lưu vực sông. Ông Nhật khẳng định, sản phẩm mà CEFD cùng các đơn vị liên danh tiến hành trong gói thầu lần này được đánh giá rất cao khi áp dụng thực tiễn cho công tác phòng chống thiên tai tại các địa phương.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Cũng trong hội thảo, TS. Lê Minh Nhật chia sẻ, vừa qua Luật mới đã thông qua quy định các tỉnh thành phải xây dựng Kế hoạch phòng chống thiên tai cấp tỉnh và tập trung xây dựng kế hoạch quản lý lũ tổng hợp trên lưu vực sông nội tỉnh. Đây là cơ sở pháp lý quan trọng cũng như là nhu cầu thực tiễn để triển khai ứng dụng các sản phẩm của gói thầu này cũng như mở rộng cho các lưu vực và các địa phương khác trong cả nước.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/cefd-tong-ket-hop-phan-2-2.png\" rel=\"newsdetailslidephoto\" title=\"Hội nghị Tổng kết Hợp phần 2 “Tăng cường năng lực phòng chống thiên tai” thuộc Dự án Khắc phục khẩn cấp hậu quả thiên tai tại một số tỉnh miền Trung (ENDRP)\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Hội nghị Tổng kết Hợp phần 2 “Tăng cường năng lực phòng chống thiên tai” thuộc Dự án Khắc phục khẩn cấp hậu quả thiên tai tại một số tỉnh miền Trung (ENDRP)\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/cefd-tong-ket-hop-phan-2-2.png\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.662px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">Sản phẩm của các gói thầu được trưng bày tại hội nghị.</em></figcaption></figure></div><table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"text-align: center; margin: 0px; padding: 5px; max-width: 100%; width: 726.4px; border: 1px solid rgb(173, 173, 173); color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 10px;\"><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">Mục tiêu chính của Dự án “Khắc phục khẩn cấp hậu quả thiên tai tại một số tỉnh miền Trung” là khắc phục cơ sở hạ tầng và tái định cư tại các tỉnh thuộc dự án bị thiên tai ảnh hưởng và tăng cường năng lực của Chính phủ để chỉ đạo, thực hiện hiệu quả với các sự kiện thiên tai trong tương lai.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">Hợp phần 2 “Tăng cường năng lực phòng chống thiên tai” thuộc Dự án do Bộ Nông nghiệp và Phát triển Nông thôn triển khai dưới sự tham mưu của Tổng cục Phòng chống thiên tai với vai trò là chủ dự án. Tổng cục Phòng, chống thiên tai sẽ cung cấp chiến lược chung, chính sách, và thực hiện điều phối chung giữa các cơ quan Chính phủ và các tỉnh để việc thực hiện Hợp phần diễn ra thuận lợi như đã đề ra. Các hoạt động chính của Hợp phần 2 gồm:</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">(1) Đánh giá mức độ hiệu quả của nỗ lực giảm thiểu nguy cơ lũ lụt hiện tại ở khu vực Miền Trung, sử dụng bản đánh giá lũ lụt năm 2016 để làm ví dụ tham khảo.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">(2) Phát triển kế hoạch quản lý tổng hợp lưu vực và sông, liên kết với kế hoạch phòng ngừa và chuẩn bị cho thiên tai cấp tỉnh.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">(3) Phát triển chuỗi hành động khắc phục khẩn cấp.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">(4) Năng lực xây dựng phòng ngừa và chuẩn bị cho thiên tai và cung cấp hướng dẫn về phương pháp đánh giá tổn thất và thiệt hại do thiên tai gây ra thông qua các khóa tập huấn và các buổi hội thảo.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">Để thực hiện Hợp phần 2, Ban Quản lý dự án “Vận hành hồ chứa trong tình huống khẩn cấp và quản lý lũ hiệu quả bằng hệ thống thông tin quản lý thiên tai toàn diện” thuộc Tổng cục Phòng, chống thiên tai, Bộ Nông nghiệp và Phát triển Nông thôn là cơ quan cấp Trung ương chịu trách nhiệm thực hiện việc giám sát, kiểm toán, đấu thầu, quản lý tài chính, hành chính hợp đồng, chi trả cho đơn vị tư vấn thực hiện gói thầu, và bàn giao.</p></td></tr></tbody></table>', '', '', '2020-08-07 02:02:40', '2020-08-07 03:05:48', NULL, '', ''),(92, 8, 'Nam sinh lớp 11 Trường THPT Chuyên Khoa học Tự nhiên giành huy chương vàng Hóa quốc tế', 'Đạt 94,08 điểm, xếp thứ 9/231 thí sinh, Nguyễn Hoàng Dương là học sinh lớp 11 duy nhất của Việt Nam giành huy chương vàng Olympic Hóa học quốc tế năm 2020.', '<p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Sáng 2/8, ba ngày sau khi biết kết quả ở Olympic Hóa học quốc tế (IChO 2020), Nguyễn Hoàng Dương, học sinh lớp 11 trường THPT chuyên Khoa học tự nhiên (Đại học Khoa học tự nhiên, Đại học Quốc gia Hà Nội) vẫn nhận được lời chúc mừng từ người quen. Ngồi chơi cùng em trai bên mô hình sân bóng tự chế bằng bìa carton, Dương bảo nhẹ nhõm khi đạt được mục tiêu ở đấu trường quốc tế.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Dương nhớ như in buổi tối 30/7. Lễ công bố kết quả IChO 2020 diễn ra từ 19h đến 20h. Em và ba anh chị lớp 12 trong đội tuyển được gọi đến Đại học Sư phạm Hà Nội để cùng nghe kết quả với ba thầy cô phụ trách đội tuyển cùng đại diện Bộ Giáo dục và Đào tạo. Khi Ban tổ chức xướng tên thí sinh được nhận bằng khen, huy chương đồng rồi bạc mà vẫn chưa thấy tên 4 thí sinh Việt Nam, tất cả sung sướng vì biết chắc sẽ có 4 huy chương vàng. Khi Ban tổ chức đọc đến tên từng thí sinh, khuôn mặt ai nấy đều rạng rỡ.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nguyen-hoang-duong-copy-8313-15963484351.jpg\" rel=\"newsdetailslidephoto\" title=\"Nam sinh lớp 11 Trường THPT Chuyên Khoa học Tự nhiên giành huy chương vàng Hóa quốc tế\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Nam sinh lớp 11 Trường THPT Chuyên Khoa học Tự nhiên giành huy chương vàng Hóa quốc tế\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nguyen-hoang-duong-copy-8313-15963484351.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Nguyễn Hoàng Dương trong phòng sách của gia đình.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Do Covid-19, phải tránh tụ tập đông người, gia đình Dương không được cùng con xem lễ công bố kết quả. Cả nhà tập trung trước tivi để xem. Thấy con là một trong 10 thí sinh có kết quả cao nhất, cả nhà bỏ cơm tối, chạy xe tới Đại học Sư phạm Hà Nội để chúc mừng và đón Dương trở về. \"Có lẽ những khoảnh khắc đó cả nhà em không bao giờ quên\", nam sinh nói.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Sinh ra trong gia đình có bố mẹ đều làm trong ngành Y, dù từ nhỏ đã yêu thích Toán học, giành nhiều thành tích như giải nhất kỳ thi Toán giữa các thành phố tổ chức tại Nga, giải High Distinction (xuất sắc) cuộc thi Toán học Australia bậc THCS (AMO), top 5% ở kỳ thi Toán học Mỹ (AMC), Dương vẫn lựa chọn theo môn Hóa ngay từ lần đầu tiếp xúc năm lớp 8.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Khi đó, chương trình học ở trường chủ yếu là lý thuyết, cả năm chỉ được xuống phòng thí nghiệm 2-3 lần, Dương mày mò, tìm thêm các thí nghiệm trên mạng. Em thích thú khi thấy sự biến chuyển chất này sang chất khác. Nhớ nhiều loại thuốc trong những đơn thuốc bố kê cho bệnh nhân, em tìm ra các chất hóa học tương ứng. Dần dần, Dương thấy Hóa học thiết thực với đời sống, em càng chắc chắn với lựa chọn của mình.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Lớp 9, Dương có hai thành tích đầu tiên với môn Hóa, gồm giải nhất cấp quận và giải ba cấp thành phố. Thi vào THPT, em đỗ ba trường chuyên. Dù học trường Hà Nội - Amsterdam từ cấp THCS, lại được trường chuyên Đại học Sư phạm Hà Nội trao học bổng, Dương quyết chọn trường THPT chuyên Khoa học tự nhiên vì từng gặp gỡ anh chị trong trường và được tiếp lửa để tham gia các cuộc thi quốc gia, quốc tế.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Vào trường, Dương được thầy giáo phát hiện có năng khiếu nên cho đi học dự thính cùng các anh chị lớp 11, 12 rồi thi chọn đội tuyển thi quốc gia. Dương vẫn nhớ khi đó có 4 tháng chuẩn bị kiến thức. Em phải học các chuyên đề trọng tâm rải rác ở lớp 10, 11 và 12 để đủ khả năng tranh suất dự vòng quốc gia.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Ngoài thời gian học trên lớp, em dành gần hết quỹ thời gian ở nhà cho Hóa. Lúc mới đầu, gặp nhiều bài khó, Dương stress nhưng cứ nghĩ đến mục tiêu phải được thi quốc gia, em lại cố gắng hơn để rồi mỗi khi giải được bài khó, thu nạp được thêm kiến thức mới, em lại càng hứng thú đào sâu. Lúc đó, không ngày nào Dương đi ngủ trước 12h đêm. \"Chưa bao giờ em đạt cường độ cao như vậy\", Dương nói. Kết quả, Dương được đi thi quốc gia và giành giải ba, trở thành học sinh lớp 10 đầu tiên của cả nước đạt giải cao như vậy.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Có thêm một năm ôn luyện, lớp 11, Dương tiếp tục được thi quốc gia và đạt giải nhất. Tuy nhiên, Covid-19 bùng phát, các kỳ thi Olympic quốc tế đều báo hoãn hoặc hủy, Dương xác định cơ hội thi quốc tế sẽ chỉ đến với em sau một năm nữa. Đang trong thế \"xả hơi\" thì đến cuối tháng 6, đầu tháng 7, Bộ Giáo dục và Đào tạo thông báo chọn đội tuyển thi Olympic Hóa học quốc tế bởi nước chủ nhà Thổ Nhĩ Kỳ sẽ tổ chức thi trực tuyến. Trở thành một trong bốn thí sinh của đội tuyển Việt Nam, Dương chỉ có 20 ngày để chuẩn bị cho kỳ thi tầm cỡ quốc tế.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Dương mô tả 20 ngày cấp tốc ôn thi quốc tế bằng câu \"Xung quanh em toàn là Hóa\". Thời gian đó, ngày nào em cũng học cùng các anh chị từ 8h sáng đến 8h tối, thậm chí 10h. Các thầy cô phụ trách đội tuyển cũng rất áp lực khi phải hướng dẫn đào sâu 25 vấn đề mà ban tổ chức đưa ra trong thời gian gấp gáp. Dương và các anh chị phải làm rất nhiều bài khó. Thế nhưng, em lại rất vui vẻ vì được thỏa mãn đam mê.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">\"Em học hỏi được rất nhiều kinh nghiệm từ các anh chị\", Dương kể. Có người mạnh về tư duy, người cẩn thận, người lại tốc độ. Thấy vậy, Dương cũng thử làm đề nhanh hơn để so với người nhanh nhất trong đội hoặc thử làm chậm lại, cẩn thận hơn xem phần trăm sai sót có giảm. Khi giải bài khó, bốn người tìm ra bốn cách, em lại so sánh, thấy cách nào hay sẽ học hỏi. Điều này giúp Dương tìm ra tốc độ, chiến thuật tư duy phù hợp khi làm bài.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Hôm thi trực tuyến, bốn thí sinh ngồi bốn góc trong căn phòng rộng ở Đại học Sư phạm Hà Nội, có camera trước, sau và camera online để thầy cô và Ban tổ chức quan sát. Sau 5 tiếng hoàn thành bài thi (từ 16h đến 21h, tương ứng với thời gian thi thực từ 13h đến 18h ở Thổ Nhĩ Kỳ), các thầy cô có 5 phút để scan bài ngay trong phòng và gửi sang cho Ban tổ chức. Dương làm được hết câu hỏi trong đề. Dù có một số ý không chắc chắn, em vẫn vào top 10 thí sinh có điểm cao nhất.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Nhiều người bảo do thi trực tuyến, không có phần thực hành nên Việt Nam mới đạt 4 huy chương vàng. Dương thấy có phần đúng nhưng không phải tất cả bởi nó không đồng nghĩa học sinh Việt Nam không có kỹ năng thực hành.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">\"Đề thi có những câu lý thuyết thực hành rất khó mà nếu thí sinh không biết làm thực hành sẽ không thể trả lời được. Hơn nữa, Việt Nam đã làm rất tốt phần thực hành trong những năm gần đây, như năm ngoái anh <a href=\"https://vnexpress.net/nam-sinh-dat-diem-tuyet-doi-phan-thuc-hanh-olympic-hoa-quoc-te-3960656.html\" rel=\"dofollow\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\">Bá Tân</a> được điểm tối đa phần này\", Dương nói, mong muốn năm sau khi được tiếp tục thi quốc tế tại Nhật Bản, em và đội tuyển Việt Nam có thể tiếp tục chứng minh điều này.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Ngồi xem hai cậu con trai chơi với nhau, gương mặt chị Nguyễn Phương Thanh ánh lên sự tự hào. Chị Thanh là Giám đốc Pháp chế và Quản lý chất lượng ngành Dược phẩm của văn phòng đại diện Tập đoàn Abbott (Mỹ) tại Việt Nam, trong khi chồng là PGS Nguyễn Văn Tuấn, Trưởng bộ môn Tâm thần (Đại học Y Hà Nội), Phó viện trưởng Viện Sức khỏe Tâm thần (Bệnh viện Bạch Mai). Vì vậy, anh chị rất bận rộn với việc giảng dạy, nghiên cứu, buộc các con phải tự lập sớm.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nguyen-hoang-duong-2-copy-9749-15963484351.jpg\" rel=\"newsdetailslidephoto\" title=\"Nam sinh lớp 11 Trường THPT Chuyên Khoa học Tự nhiên giành huy chương vàng Hóa quốc tế\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Nam sinh lớp 11 Trường THPT Chuyên Khoa học Tự nhiên giành huy chương vàng Hóa quốc tế\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nguyen-hoang-duong-2-copy-9749-15963484351.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Dương bên bố mẹ và em trai. </figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Chị Thanh kể Dương rất tự lập, đặc biệt trong việc lập kế hoạch học tập. Ví dụ, trước khi thi quốc tế, con bảo sẽ giải toàn bộ đề các năm trước, tìm các bài báo của chủ nhân giải Nobel Hóa học của nước chủ nhà để đọc. Hay việc con thích và tự giác đọc sách, đến mức mọi ngóc ngách trong nhà, kể cả toilet, đều thấy sách của Dương, từ truyện tranh đến sách khoa học. \"Huy chương vàng Olymlic Hóa học quốc tế xứng đáng với những nỗ lực của con\", chị Thanh nói.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Thầy Vi Anh Tuấn, Hiệu phó trường THPT chuyên Khoa học tự nhiên, người dạy Hóa cho Dương ở trường, cũng đánh giá Dương xứng đáng với huy chương vàng vừa nhận được. Thầy Tuấn biết đến Dương khi giành giải nhất học sinh giỏi Hóa cấp quận hồi lớp 9. Khi đó, thầy đã liên hệ, bảo Dương đến trường tham quan, làm quen với các anh chị khóa trước để có định hướng, đam mê thêm về môn Hóa.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Dương không phải học sinh đầu tiên của trường Khoa học tự nhiên đạt huy chương vàng quốc tế từ lớp 11 nên thầy Tuấn không bất ngờ khi đón nhận kết quả của em hôm 30/7. Dù vậy, thầy vẫn rất vui và tự hào. \"Một học sinh lớp 11 đứng thứ 9 trong một cuộc thi quốc tế có 231 thí sinh từ 60 quốc gia và vùng lãnh thổ tham dự là rất hiếm\", thầy Tuấn khẳng định.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Thầy Nguyễn Ngọc Hà, Trưởng khoa Hóa Đại học Sư Phạm Hà Nội, Trưởng đoàn Olympic Hóa học của Việt Nam, đánh giá rất cao về Dương dù chỉ hướng dẫn em trong 20 ngày trước kỳ thi quốc tế. \"Dương thông minh, luôn tìm ra điểm mấu chốt của bài rất sớm. Dù kém các anh chị trong đội tuyển một năm, khoảng cách kiến thức của em và các anh chị gần như không có\", thầy Hà nói.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trở về cuộc sống bình thường sau tấm huy chương vàng, Dương dành nhiều thời gian cho các sở thích cá nhân như đọc sách, chơi piano, thể thao. Dương hy vọng tiếp tục được đi thi quốc tế và bảo vệ được tấm huy chương vàng trong năm sau.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: right; font-family: Roboto, sans-serif;\">(Theo Vnexpress)</p>', '', '', '2020-08-07 02:03:15', '2020-08-25 02:36:49', NULL, '/php-intership-team/admin/SourceFile/upload/nguyen-hoang-duong-copy-8313-15963484351.jpg', ''),(93, 8, 'Không khí nhập học vào lớp 10 THPT Chuyên Khoa học Tự nhiên năm 2020', 'Sau những tháng ngày miệt mài học hành và thi cử căng thẳng, ngày 01/08/2020 Trường THPT Chuyên Khoa học Tự nhiên (KHTN), Trường Đại học Khoa học Tự nhiên (ĐHKHTN), Đại học Quốc gia Hà Nội vui mừng chào đón 548 bạn học sinh trúng tuyển vào lớp 10 khóa 2020 - 2023 tới nhập học.', '<div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nhap-hoc-lop-10-chuyen-khtn-2020-2.png\" rel=\"newsdetailslidephoto\" title=\"Không khí nhập học vào lớp 10 THPT Chuyên Khoa học Tự nhiên năm 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Không khí nhập học vào lớp 10 THPT Chuyên Khoa học Tự nhiên năm 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nhap-hoc-lop-10-chuyen-khtn-2020-2.png\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.662px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Công tác nhập học cho các bạn học sinh lớp 10 Trường THPT Chuyên KHTN được tổ chức trong cả ngày 01/08/2020 tại 182 Lương Thế Vinh, Thanh Xuân, Hà Nội. Để giúp phụ huynh và học sinh thuận tiện trong quá trình nhập học, mọi thông tin về hồ sơ và thời gian nhập học đã được Nhà trường thông báo đầy đủ trước đó. Nhằm đảm bảo giãn cách và an toàn xã hội trong mùa dịch Covid-19, Nhà trường đã tổ chức nhập học theo ca, sắp xếp vị trí cho phụ huynh và học sinh đảm bảo khoảng cách an toàn cũng như tiến hành rửa tay sát khuẩn, đo thân nhiệt ngay từ cổng trường. Theo đó các bạn học sinh nhập học vào lớp chuyên Sinh học, Toán học và Tin học sẽ làm thủ tục vào buổi sáng từ 8h00 tới 11h30, buổi chiều từ 13h30 tới 16h30 là thời gian dành cho các bạn lớp chuyên Vật lý, Hóa học và Chất lượng cao. Ngoài ra ngay trong ngày nhập học, các bậc phụ huynh và các bạn học sinh có nhu cầu cũng sẽ tiến hành các thủ tục đăng ký nội trú và tạm trú tại Ký túc xá Mễ Trì.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nhap-hoc-lop-10-chuyen-khtn-2020-1.png\" rel=\"newsdetailslidephoto\" title=\"Không khí nhập học vào lớp 10 THPT Chuyên Khoa học Tự nhiên năm 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Không khí nhập học vào lớp 10 THPT Chuyên Khoa học Tự nhiên năm 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nhap-hoc-lop-10-chuyen-khtn-2020-1.png\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.662px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">Từ sớm các bậc phụ huynh và các bạn học sinh đã tới trường để làm thủ tục nhập học theo đúng quy định và thông báo của Nhà trường.</em></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Theo chia sẻ từ TS. Nguyễn Thanh Bình - Trưởng phòng Chính trị và Công tác Sinh viên, Trường ĐHKHTN cho biết: “năm nay Trường THPT Chuyên KHTN có tổng cộng 548 em học sinh nhập học tại 5 khối chuyên (Toán học, Vật Lý, Hóa học, Sinh học và Tin học) và các lớp chất lượng cao”.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nhap-hoc-lop-10-chuyen-khtn-2020-3.png\" rel=\"newsdetailslidephoto\" title=\"Không khí nhập học vào lớp 10 THPT Chuyên Khoa học Tự nhiên năm 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Không khí nhập học vào lớp 10 THPT Chuyên Khoa học Tự nhiên năm 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/nhap-hoc-lop-10-chuyen-khtn-2020-3.png\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.662px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">Nhà trường sắp xếp vị trí đảm bảo đảm đủ khoảng cách an toàn cho các bậc phụ huynh và các bạn học sinh tới nhập học trong mùa Covid-19.</em></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Hân hoan trong buổi nhập học, em Kiều Phương Bảo Khánh - tân học sinh khối chuyên Sinh chia sẻ: “Em cảm thấy rất vui khi sau một năm học tập và ôn thi rất vất vả tới nay đã đạt được kết quả tốt, biến ước mơ thành hiện thực”.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Phấn khởi cùng con nhập học, chị Liễu - phụ huynh em Tạ Quốc Hùng - tân học sinh khối chuyên Tin học tâm sự: “Gia đình cảm thấy rất vui khi em đỗ vào lớp chuyên Tin Trường THPT Chuyên KHTN. Lúc đầu gia đình cũng chỉ định cho em thử sức thôi, nhưng qua những tìm hiểu gia đình thầy những hoạt động tại Trường rất hiệu quả cùng với đó là Hùng quyết tâm muốn được theo học tại Trường nên gia đình đã ủng hộ quyết định của em”.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Sau ngày nhập học, các em tân học sinh Trường THPT Chuyên KHTN sẽ chính thức theo học tại trường. Mọi thông tin về lớp học, lịch học, thời khóa biểu sẽ được nhà trường cập nhật đầy đủ tại địa chỉ: <a href=\"http://hsgs.edu.vn/\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\">http://hsgs.edu.vn</a>.</p>', '', '', '2020-08-07 03:07:44', '2020-08-25 02:37:48', NULL, '/php-intership-team/admin/SourceFile/upload/hsgs_AB-1024x585.jpg', ''),(94, 8, 'Trường Đại học Khoa học Tự nhiên tổ chức tập huấn công tác coi thi kỳ thi tốt nghiệp THPT năm 2020', 'Ngày 30/7/2020, tại Hội trường tầng 7 nhà T5, Trường Đại học Khoa học Tự nhiên tổ chức tập huấn cho các cán bộ, giảng viên tham gia kiểm tra, giám sát kỳ thi tốt nghiệp THPT năm 2020.', '<p><br></p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc08732-1.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường  Đại học Khoa học Tự nhiên tổ chức tập huấn công tác coi thi kỳ thi tốt nghiệp THPT năm 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường  Đại học Khoa học Tự nhiên tổ chức tập huấn công tác coi thi kỳ thi tốt nghiệp THPT năm 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc08732-1.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Toàn cảnh buổi tập huấn.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trong kỳ thi tốt nghiệp THPT năm nay, Trường Đại học Khoa học Tự nhiên huy động  hơn 160 cán bộ, giảng viên (có số dư dự phòng) tham gia làm nhiệm vụ kiểm tra, giám sát công tác coi thi tại 9 quận, huyện của Hà Nội. Các địa điểm Đoàn sẽ thực hiện nhiệm vụ là: huyện Chương Mỹ, huyện Thanh Trì, huyện Thường Tín, huyện Phú Xuyên, huyện Thanh Oai, huyện Ứng Hòa, quận Đống Đa, quận Cầu Giấy, quận Tây Hồ.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc08746-1.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường  Đại học Khoa học Tự nhiên tổ chức tập huấn công tác coi thi kỳ thi tốt nghiệp THPT năm 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường  Đại học Khoa học Tự nhiên tổ chức tập huấn công tác coi thi kỳ thi tốt nghiệp THPT năm 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc08746-1.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">TS. Nguyễn Thanh Tùng, Trưởng phòng Nghiệp vụ 2, Thanh tra Bộ GD&ĐT đang giải đáp các tình huống cụ thể cho các cán bộ tham gia tập huấn.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Tại buổi tập huấn, các cán bộ, giảng viên tham gia kiểm tra, giám sát kỳ thi tốt nghiệp THPT năm 2020 đã được nghe phổ biến về cách tổ chức và quy chế Kỳ thi tốt nghiệp THPT 2020,  quy trình kiểm tra công tác tổ chức coi thi, đồng thời được tập huấn nghiệp vụ kiểm tra, giám sát đảm bảo đúng quy chế của Bộ GD&ĐT. Những thắc mắc về các tình huống cụ thể đã được cán bộ thanh tra Bộ GD&ĐT giải đáp. Kết thúc buổi tập huấn, các cán bộ làm bài tập trắc nghiệm với Bộ câu hỏi kiểm tra nghiệp vụ thanh tra/kiểm tra coi thi tốt nghiệp THPT 2020 để đảm bảo đã nắm được nội dung của buổi tập huấn và có thể làm tốt nhiệm vụ.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc08751.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường  Đại học Khoa học Tự nhiên tổ chức tập huấn công tác coi thi kỳ thi tốt nghiệp THPT năm 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường  Đại học Khoa học Tự nhiên tổ chức tập huấn công tác coi thi kỳ thi tốt nghiệp THPT năm 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc08751.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">PGS.TS. Ngạc An Bang, Phó Hiệu trưởng, Trường Đại học Khoa học Tự nhiên đang phổ biến quy chế Kỳ thi tốt nghiệp THPT năm 2020.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Theo PGS.TSKH. Vũ Hoàng Linh, Hiệu trưởng Trường ĐH Khoa học tự nhiên, nhận thức việc kiểm tra, giám sát kỳ thi tốt nghiệp THPT năm 2020 là nhiệm vụ chính trị quan trọng mà Bộ GD&ĐT tin tưởng giao phó, bên cạnh việc huy động cán bộ, giảng viên tham gia kiểm tra, giám sát kỳ thi tốt nghiệp THPT năm 2020 (ưu tiên những cán bộ có kinh nghiệm), Ban giám hiệu đã họp với lãnh đạo các đơn vị để phổ biến và quán triệt tinh thần chỉ đạo của Bộ.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Năm 2020, Trường Đại học Khoa học Tự nhiên sử dụng phương án xét tuyển dựa trên kết quả Kỳ thi THPT, do vậy tập thể cán bộ nhà trường sẽ cố gắng để thực hiện tốt công việc, để kỳ thi diễn ra an toàn và thành công, thể hiện trách nhiệm đối với xã hội và với bản thân Nhà trường trong việc xét tuyển đầu vào.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\"><img alt=\"\" src=\"http://hus.vnu.edu.vn/spacer.gif\" style=\"margin: 0px; padding: 0px; height: 368px; max-width: 100%; width: 551px;\"></p>', '', '', '2020-08-07 03:10:18', '2020-08-25 02:35:27', NULL, '/php-intership-team/admin/SourceFile/upload/dsc08732-1.jpg', ''),(123, 8, 'ádasdas', 'ádasda', '<p>ádasdas</p>', '', '', '2020-08-24 03:51:17', '2020-08-25 01:18:05', '123', '/php-intership-team/admin/SourceFile/upload/IMG_0203.JPG', ''),(124, 8, 'áasdasdasd', 'aa', '<p>aaaa</p>', '', '', '2020-08-24 08:07:29', '2020-08-25 01:18:15', '124', '/php-intership-team/admin/SourceFile/upload/IMG_0203.JPG', ''),(125, 8, 'sadasdas', '', '<p>aa</p>', '', '', '2020-08-24 08:26:56', '2020-08-25 01:18:25', '125', 'ád', ''),(126, 2, 'Peter ', 'Boring', 'Tired', '', '', '2020-08-18 16:30:58', '2020-08-24 09:58:30', '126', '', ''),(127, 8, 'Sinh viên Trương Tấn Sang - Thủ khoa đầu ra năm 2020 của Trường Đại học Khoa học Tự nhiên được thành phố Hà Nội xét chọn tuyên dương', 'Trương Tấn Sang là sinh viên K61 Cử nhân tài năng Hóa học, tốt nghiệp thủ khoa đầu ra của Trường Đại học Khoa học Tự nhiên năm 2020 với số điểm gần tuyệt đối: 3.95/4, xếp loại Xuất sắc', '<div><br></div><div><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/dsc07316.jpg\" rel=\"newsdetailslidephoto\" title=\"Sinh viên Trương Tấn Sang - Thủ khoa đầu ra năm 2020 của Trường Đại học Khoa học Tự nhiên được thành phố Hà Nội xét chọn tuyên dương \" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Sinh viên Trương Tấn Sang - Thủ khoa đầu ra năm 2020 của Trường Đại học Khoa học Tự nhiên được thành phố Hà Nội xét chọn tuyên dương \" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/dsc07316.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Sinh viên Trương Tấn Sang phát biểu tại Lễ bế giảng và trao bằng Cử nhân khoa học hệ Đại học chính quy năm 2020 của Trường Đại học Khoa học Tự nhiên.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Theo thông tin từ Thành Đoàn Hà Nội, Hội đồng xét chọn Thủ khoa xuất sắc tốt nghiệp các trường đại học, học viện trên địa bàn thành phố Hà Nội đã họp và thống nhất thông qua hồ sơ đề nghị xét chọn Thủ khoa xuất sắc của các trường đại học, học viện, trong đó có Trường Đại học Khoa học Tự nhiên. Theo đó, sinh viên Trương Tấn Sang (sinh viên ngành Hóa học, khoa Hóa học, Trường Đại học Khoa học Tự nhiên) đã được xét chọn là một trong những thủ khoa xuất sắc tốt nghiệp các trường đại học, học viện trên địa bàn thành phố Hà Nội năm 2020 và sẽ được thành phố Hà Nội tuyên dương, khen thưởng.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trương Tấn Sang là sinh viên có nhiều thành tích học tập đặc biệt nổi trội. Trong 4 năm học tập tại Trường Đại học Khoa học Tự nhiên, Sang luôn đứng đầu khoá học. Em đã được kết nạp Đảng ngay từ năm thứ 2. Đặc biệt, Sang từng đạt giải nhất Olympic Hóa học sinh viên toàn quốc năm 2018. Với các thành tích của mình Sang đạt được nhiều học bổng rất uy tín như: Giải thưởng Honda Y-E-S, học bổng UOP của Tập đoàn Honeywell (Hoa Kỳ)&nbsp;».</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/083b15c3-a4df-4c4c-ab30-e1ba4c2e1112.jpeg\" rel=\"newsdetailslidephoto\" title=\"Sinh viên Trương Tấn Sang - Thủ khoa đầu ra năm 2020 của Trường Đại học Khoa học Tự nhiên được thành phố Hà Nội xét chọn tuyên dương \" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Sinh viên Trương Tấn Sang - Thủ khoa đầu ra năm 2020 của Trường Đại học Khoa học Tự nhiên được thành phố Hà Nội xét chọn tuyên dương \" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/08/083b15c3-a4df-4c4c-ab30-e1ba4c2e1112.jpeg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Sinh viên Trương Tấn Sang nhận Giải thưởng Honda Y-E-S.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Tháng 5/2020, khi ngày tốt nghiệp đại học cận kề, Trương Tấn Sang đã được Đại học École Polytechnique (một trong những trường đại học lớn nhất và danh tiếng nhất của Pháp) chấp thuận theo học chương trình thạc sĩ 2, đồng thời em cũng đạt học bổng Paris Saclay để học thạc sĩ 2 tại trường.&nbsp;<br style=\"margin: 0px; padding: 0px;\">Dự kiến, Lễ tuyên dương các Thủ khoa xuất sắc tốt nghiệp các trường đại học, học viện trên địa bàn thành phố Hà Nội sẽ diễn ra vào cuối tháng 8/2020.&nbsp;</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Năm 2020 là năm thứ 18 liên tiếp thành phố Hà Nội tổ chức tuyên dương thủ khoa xuất sắc tốt nghiệp các trường đại học, học viện trên địa bàn. Nhiều thủ khoa được vinh danh đã thành công trong sự nghiệp, có nhiều đóng góp cho Thủ đô và đất nước.</p></div>', '', '', '2020-08-24 09:37:37', '2020-08-24 09:38:49', NULL, '/php-intership-team/admin/SourceFile/upload/dsc07316.jpg', ''),(128, 5, 'Đảng bộ Trường Đại học Khoa học Tự nhiên hoàn thành việc chuẩn y cấp ủy các chi bộ nhiệm kỳ 2020 - 2022, tiến tới Đại hội đại biểu Đảng bộ lần thứ XXVII, nhiệm kỳ 2020 - 2025', 'Thực hiện kế hoạch số 237-KH/ĐU ngày 14/8/2019 của Đảng bộ Đại học Quốc gia Hà Nội (ĐHQGHN) về việc tổ chức Đại hội đảng bộ, chi bộ các cấp tiến tới Đại hội đại biểu Đảng bộ ĐHQGHN lần thứ VI, nhiệm kỳ 2020-2025, từ ngày 23/3 đến ngày 12/5/2020, 12 chi bộ thuộc Đảng bộ trường Đại học Khoa học Tự nhiên bao gồm: Chi bộ các khoa Hóa học, Vật lý, Khí tượng Thủy văn & Hải dương học, Địa lý, Môi trường, Toán - Cơ - Tin học, Sinh học, Địa chất, Chi bộ khối Phòng ban I, II, III và Chi bộ trường THPT chuyên Khoa học Tự nhiên đã tổ chức thành công Đại hội chi bộ nhiệm kỳ 2020 - 2022.', '<p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">Thực hiện kế hoạch số 237-KH/ĐU ngày 14/8/2019 của Đảng bộ Đại học Quốc gia Hà Nội (ĐHQGHN) về việc tổ chức Đại hội đảng bộ, chi bộ các cấp tiến tới Đại hội đại biểu Đảng bộ ĐHQGHN lần thứ VI, nhiệm kỳ 2020-2025, từ ngày 23/3 đến ngày 12/5/2020, 12 chi bộ thuộc Đảng bộ trường Đại học Khoa học Tự nhiên bao gồm: Chi bộ các khoa Hóa học, Vật lý, Khí tượng Thủy văn &amp; Hải dương học, Địa lý, Môi trường, Toán - Cơ - Tin học, Sinh học, Địa chất, Chi bộ khối Phòng ban I, II, III và Chi bộ trường THPT chuyên Khoa học Tự nhiên đã tổ chức thành công Đại hội chi bộ nhiệm kỳ 2020 - 2022.</span></p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">&nbsp;</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/dh-chi-bo-khoa-hoa-hoc.jpg\" rel=\"newsdetailslidephoto\" title=\"Đảng bộ Trường Đại học Khoa học Tự nhiên hoàn thành việc chuẩn y cấp ủy các chi bộ nhiệm kỳ 2020 - 2022, tiến tới Đại hội đại biểu Đảng bộ lần thứ XXVII, nhiệm kỳ 2020 - 2025\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Đảng bộ Trường Đại học Khoa học Tự nhiên hoàn thành việc chuẩn y cấp ủy các chi bộ nhiệm kỳ 2020 - 2022, tiến tới Đại hội đại biểu Đảng bộ lần thứ XXVII, nhiệm kỳ 2020 - 2025\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/dh-chi-bo-khoa-hoa-hoc.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Đại hội chi bộ Khoa Hóa học</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: Tahoma, Geneva, sans-serif;\">Đến nay, Đảng ủy Trường Đại học Khoa học Tự nhiên đã ban hành xong các quyết định chuẩn y các chức danh trong Chi ủy, Chi bộ nhiệm kỳ 2020-2022. Về phía các chi bộ, công tác viết tham luận đang được thực hiện khẩn trương để kịp báo cáo tại Đại hội đại biểu Đảng bộ trường lần thứ XXVII, nhiệm kỳ 2020 - 2025 (dự định tổ chức trong hai ngày 27 và 28/6/2020).</span></span></p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/khoi-ii.jpg\" rel=\"newsdetailslidephoto\" title=\"Đảng bộ Trường Đại học Khoa học Tự nhiên hoàn thành việc chuẩn y cấp ủy các chi bộ nhiệm kỳ 2020 - 2022, tiến tới Đại hội đại biểu Đảng bộ lần thứ XXVII, nhiệm kỳ 2020 - 2025\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Đảng bộ Trường Đại học Khoa học Tự nhiên hoàn thành việc chuẩn y cấp ủy các chi bộ nhiệm kỳ 2020 - 2022, tiến tới Đại hội đại biểu Đảng bộ lần thứ XXVII, nhiệm kỳ 2020 - 2025\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/khoi-ii.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Đảng viên tham gia bỏ phiếu ở Đại hội chi bộ khối Phòng ban II</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: Tahoma, Geneva, sans-serif;\">Trước khi 12 chi bộ thuộc Đảng bộ Trường Đại học Khoa học Tự nhiên tổ chức đại hội, Tổ công tác của Đảng ủy và Ủy ban kiểm tra Đảng ủy Nhà trường đã lần lượt làm việc với các Chi bộ nhằm rà soát công tác chuẩn bị Đại hội và Đề án nhân sự nhiệm kỳ 2020-2022. Tiếp đó, các chi bộ đã lần lượt tổ chức đại hội nhằm thực hiện các nội dung: Tổng kết thực hiện nghị quyết Đại hội chi bộ nhiệm kỳ 2017-2020 và xác định phương hướng, mục tiêu, nhiệm vụ, giải pháp của chi bộ nhiệm kỳ 2020-2022; &nbsp;Thảo luận, đóng góp ý kiến vào Dự thảo các văn kiện Đại hội XIII của Đảng và Dự thảo văn kiện Đại hội Đảng bộ Trường; Bầu Ban chấp hành Chi bộ nhiệm kỳ 2020-2022; Bầu đoàn đại biểu dự đại hội Đảng bộ Trường.</span></span></p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/pgs.ts-nguyen-minh-huan-bi-thu-chi-bo-nhiem-ky-2017-2020-tang-hoa-chuc-mung-bch-chi-bo-moi-nhiem-ky-2020-2022.jpg\" rel=\"newsdetailslidephoto\" title=\"Đảng bộ Trường Đại học Khoa học Tự nhiên hoàn thành việc chuẩn y cấp ủy các chi bộ nhiệm kỳ 2020 - 2022, tiến tới Đại hội đại biểu Đảng bộ lần thứ XXVII, nhiệm kỳ 2020 - 2025\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Đảng bộ Trường Đại học Khoa học Tự nhiên hoàn thành việc chuẩn y cấp ủy các chi bộ nhiệm kỳ 2020 - 2022, tiến tới Đại hội đại biểu Đảng bộ lần thứ XXVII, nhiệm kỳ 2020 - 2025\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/pgs.ts-nguyen-minh-huan-bi-thu-chi-bo-nhiem-ky-2017-2020-tang-hoa-chuc-mung-bch-chi-bo-moi-nhiem-ky-2020-2022.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">PGS.TS Nguyễn Minh Huấn, Bí Thư chi bộ khoa Khí tượng, Thủy văn và Hải dương học nhiệm kỳ 2017-2020 tặng hoa chúc mừng Ban chấp hành Chi bộ mới nhiệm kỳ 2020-2022.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\"><span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: Tahoma, Geneva, sans-serif;\">Hiện nay, Đảng bộ trường Đại học Khoa học Tự nhiên có 12 chi bộ với 342 Đảng viên, trong đó có 315 Đảng viên chính thức, 27 Đảng viên dự bị. Đảng viên trẻ tuổi nhất là 19 tuổi.</span></span></p><table border=\"1\" cellpadding=\"10\" cellspacing=\"1\" style=\"margin: 0px; padding: 5px; max-width: 100%; width: 500px; border: 1px solid rgb(173, 173, 173); color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 10px;\"><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">Danh sách Bí thư các chi bộ đã được Đảng ủy Trường Đại học Khoa học Tự nhiên chuẩn y:</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">1. Đồng chí Trần Mạnh Cường, Bí thư chi bộ khoa Toán - Cơ - Tin học.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">2. Đồng chí Ngạc An Bang, Bí thư chi bộ khoa Vật lý.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">3. Đồng chí Lê Thanh Sơn, Bí thư chi bộ khoa Hóa học.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">4. Đồng chí Nguyễn Quang Huy, Bí thư chi bộ khoa Sinh học.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">5. Đồng chí Trần Văn Tuấn, Bí thư chi bộ khoa Địa lý.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">6. Đồng chí Đinh Xuân Thành, Bí thư chi bộ khoa Địa chất.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">7. Đồng chí Công Thanh, Bí thư chi bộ khoa Khí tượng, Thủy văn và Hải dương học.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">8. Đồng chí Nguyễn Mạnh Khải, Bí thư chi bộ khoa Môi trường.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">9. Đồng chí Lê Công Lợi, Bí thư chi bộ Trường THPT Chuyên Khoa học Tự nhiên.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">10. Đồng chí Nguyễn Thanh Bình, Bí thư chi bộ khối Phòng ban I.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">11. Đồng chí Võ Thế Đại, Bí thư chi bộ khối Phòng ban II.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">12. Đồng chí Trần Quốc Bình, Bí thư chi bộ khối Phòng ban III.</p></td></tr></tbody></table>', '', '', '2020-08-24 09:44:37', '2020-08-25 06:33:50', NULL, '/php-intership-team/admin/SourceFile/upload/pgs.ts-nguyen-minh-huan-bi-thu-chi-bo-nhiem-ky-2017-2020-tang-hoa-chuc-mung-bch-chi-bo-moi-nhiem-ky-2020-2022.jpg', ''),(129, 6, 'Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020', 'Góp mặt tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020 sáng ngày 21/06/2020 vừa qua, Trường Đại học Khoa học Tự nhiên (ĐHKHTN), Đại học Quốc gia Hà Nội đã thu hút đông đảo học sinh và phụ huynh quan tâm tới tìm hiểu, lắng nghe tư vấn về những chương trình đào tạo, cơ chế tuyển sinh hấp dẫn của nhà trường trong kỳ tuyển sinh Đại học sắp tới.', '<p><br></p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-2020.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-2020.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">Ban tư vấn của Trường ĐHKHTN tại Ngày hội tuyển sinh - hướng nghiệp 2020.</em></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Ngày hội tư vấn tuyển sinh - hướng nghiệp do báo Tuổi trẻ phối hợp cùng Bộ GD-ĐT, Tổng cục Dạy nghề (Bộ lao động Thương binh và Xã hội), Sở GD-ĐT Hà Nội và Trường Đại học Bách Khoa Hà Nội tổ chức là điểm đến không thể thiếu cho các bạn học sinh và quý phụ huynh mỗi mùa tuyển sinh.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Tại gian tư vấn của Trường ĐHKHTN, quý phụ huynh và học sinh đã được Ban tư vấn gồm các thầy cô là cán bộ, giảng viên của trường cung cấp những thông tin mới nhất về phương thức xét tuyển đại học 2020, chương trình đào tạo mới mở, chính sách học bổng, học phí,... Ngoài ra, với những bạn đã có định hướng “gia nhập” mái nhà Tự nhiên còn được các thầy cô tư vấn chuyên sâu về ngành học, môi trường học tập, thực tập, việc làm và các hoạt động phong phú của Nhà trường.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">Trường ĐHKHTN thu hút học sinh bởi phương thức xét tuyển thẳng học sinh Chuyên cùng nhiều hình thức tuyển sinh đa dạng khác</span></p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Một trong những điểm thu hút sự quan tâm của rất nhiều quý phụ huynh và các bạn học sinh đó là các phương thức tuyển sinh đa dạng của Nhà trường. Trong đó hấp dẫn nhất phải kể đến phương thức xét tuyển thẳng.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Theo quy định của Đại học Quốc gia Hà Nội năm 2020, các đối tượng thuộc diện xét tuyển thẳng bao gồm:</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">- Học sinh hệ chuyên (THPT Chuyên thuộc ĐHQGHN);</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">- Học sinh hệ chuyên (THPT Chuyên ngoài ĐHQGHN);</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">- Học sinh hệ không chuyên THPT thuộc ĐHQGHN;</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">- Học sinh THPT là thành viên tham gia cuộc thi tháng “Đường lên đỉnh Olympia”;</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">- Học sinh THPT đạt giải Nhất, Nhì, Ba học sinh giỏi cấp tỉnh/ thành phố trực thuộc trung ương.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Là một trong số những học sinh quan tâm tới phương thức xét tuyển thẳng của nhà trường, học sinh Đàm Tuấn Quỳnh, học sinh chuyên Hóa của Trường THPT Chuyên Khoa học Tự nhiên (ĐHKHTN) đã tới với Ngày hội tư vấn để tìm hiểu về việc xét tuyển thẳng vào khoa Vật lý. Chia sẻ với thầy cô, học sinh Đàm Tuấn Quỳnh cho biết, em có dự định theo đuổi ngành Vật lý lý thuyết nên rất mong có cơ hội được học tập tại trường trong tương lai.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Cùng mong mỏi giống bạn Đàm Tuấn Quỳnh, một bạn nam sinh tới từ trường Chuyên Bắc Ninh cũng tìm hiểu cơ hội được xét tuyển thẳng vào Trường ĐHKHTN. Với niềm đam mê Toán học và thành tích 2 năm liên tiếp đạt học sinh giỏi cấp tính, bạn mong muốn sẽ được theo học tại khoa Toán - Cơ - Tin học của Nhà trường.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/hoc-sinh-chuyen-bac-ninh-ngay-hoi-tu-van-tuyen-sinh-2020.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/hoc-sinh-chuyen-bac-ninh-ngay-hoi-tu-van-tuyen-sinh-2020.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">TS. Trương Thanh Tú tư vấn cho nam sinh tới từ Trường THPT chuyên Bắc Ninh về phương thức xét tuyển thẳng của Nhà trường.</em></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Bên cạnh phương thức xét tuyển thẳng, các bạn sinh viên sở hữu các chứng chỉ SAT, A-level, IELTS, ACT sẽ có lợi thế khi đăng ký xét tuyển vào Trường.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">Những ngành học đầy sức hút của Trường ĐHKHTN trong mùa tuyển sinh Đại học 2020</span></p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Với vị thế của trường đại học hàng đầu Việt Nam trong lĩnh vực khoa học tự nhiên, các ngành đào tạo truyền thống của Nhà trường luôn có sức hút với các bạn học sinh.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Học sinh Phạm Thị Phương Anh, lớp 12 chuyên Sinh, Trường THPT Lê Hồng Phong (Nam Định) cho biết: “Em quan tâm tới khoa Sinh của Trường ĐHKHTN. Mong muốn của em là được theo đuổi chuyên sâu về sinh học, nghiên cứu về nhựa sinh học. Nguyện vọng 1 của em là Trường ĐHKHTN, nguyện vọng 2 sẽ là khối ngành Kinh tế. Được biết hôm nay có Ngày hội tư vấn tuyển sinh, en cùng bạn bè đến đây để tìm hiểu thêm thông tin về ngành mình định chọn.”</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/pham-thi-phuong-anh-ngay-hoi-tu-van-tuyen-sinh-2020.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/pham-thi-phuong-anh-ngay-hoi-tu-van-tuyen-sinh-2020.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">Học sinh Phạm Thị Phương Anh, lớp 12 chuyên Sinh, Trường THPT Chuyên Lê Hồng Phong, Nam Định (áo trắng ngoài cùng bên trái) đang lắng nghe tư vấn về chương trình đào tạo tại Khoa Sinh học của Nhà trường.</em></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Cũng đến từ Trường chuyên Lê Hồng Phong, học sinh Trần Thị Kim Chi chia sẻ: “Em học chuyên Hóa, em quan tâm tới ngành Hóa Dược của Trường ĐHKHTN. Thầy giáo dạy Hóa ở trường em dạy rất hay khiến em cảm thấy thích thú với môn này. Em hi vọng có thể học Hóa Dược và được tiếp xúc với nhiều thầy dạy giỏi.”</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Tại Ngày hội Tư vấn Tuyển sinh - hướng nghiệp, TS. Trương Thanh Tú - Phó Trưởng Phòng Đào tạo Trường ĐHKHTN chia sẻ: “Một trong những điểm mới tại kỳ tuyển sinh Đại học 2020 của Nhà trường đó là sự xuất hiện của 5 ngành học mới (gồm có: Khoa học dữ liệu, Kỹ thuật điện tử và tin học, Quản lý phát triển đô thị và bất động sản, Khoa học và công nghệ thực phẩm, Công nghệ quan trắc và giám sát tài nguyên môi trường). Những chương trình này được xây dựng rất sát với nhu cầu về nguồn lao động chất lượng cao của xã hội hiện nay.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Mặc dù là những ngành học mới, nhưng do tính thực tiễn cao, nên những ngành học này đã thu hút được rất nhiều sự quan tâm của quý phụ huynh và các bạn học sinh.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">Một số hình ảnh tại Ngày hội tư vấn tuyển sinh của Trường Đại học Khoa học Tự nhiên năm 2020</span></p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-2020-1.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-2020-1.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">PGS.TSKH. Vũ Hoàng Linh - Hiệu trưởng Trường ĐHKHTN cùng các thầy cô trong ban tư vấn của Nhà trường tại Ngày hội tư vấn - hướng nghiệp năm 2020.</em></figcaption></figure></div><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-2020-2.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-2020-2.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">&nbsp;</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-4.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-4.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">Gian tư vấn của Trường ĐHKHTN luôn chật kín các quý phụ huynh và các bạn học sinh quan tâm tới thông tin tuyển sinh và chương trình đào tạo của Nhà trường.</em></figcaption></figure></div><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-3.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường ĐHKHTN hội ngộ sôi nổi cùng các sĩ tử 2K2 tại Ngày hội tư vấn tuyển sinh - hướng nghiệp 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/ngay-hoi-tu-van-tuyen-sinh-3.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\"><em style=\"margin: 0px; padding: 0px;\">Bất chấp thời tiết nắng nóng, oi bức, các thầy cô luôn nhiệt tình tư vấn và giải đáp mọi thắc mắc từ quý phụ huynh và các bạn học sinh.</em></figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Để có thêm những thông tin chi tiết và chính xác nhất về quy chế tuyển sinh, các chương trình đào tạo của Nhà trường, quý phụ huynh và các bạn học sinh có thể truy cập cổng thông tin tuyển sinh theo địa chỉ:&nbsp;<a href=\"http://tuyensinh.hus.vnu.edu.vn/\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\">http://tuyensinh.hus.vnu.edu.vn/</a>.</p>', '', '', '2020-08-24 09:51:35', '2020-08-24 09:51:35', NULL, '/php-intership-team/admin/SourceFile/upload/ngay-hoi-tu-van-tuyen-sinh-2020-1.jpg', ''),(130, 1, 'Gặp mặt học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của mình, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên Khoa học Tự nhiên”', 'Sáng ngày 23/06/2020, tại giảng đường Phan Đức Chính, Trường Đại học Khoa học Tự nhiên (ĐHKHTN), Đại học Quốc gia Hà Nội đã tổ chức buổi gặp mặt 23 học sinh Trường THPT Chuyên Khoa học Tự nhiên (KHTN) chuẩn bị tham dự kỳ thi chọn các đội tuyển quốc gia thi Olympic quốc tế năm 2020. “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của bản thân, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên KHTN” là lời nhắn nhủ của Hiệu trưởng Trường ĐHKHTN tới các em học sinh.', '<p><br></p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">Tham dự chương trình có sự hiện diện của PGS.TSKH. Vũ Hoàng Linh - Hiệu trưởng Trường ĐHKHTN, ĐHQGHN, lãnh đạo các phòng ban của Nhà trường, lãnh đạo Trường THPT Chuyên KHTN, các thầy cô, các vị phụ huynh và 23 học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020.</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">Phát biểu tại buổi gặp mặt, PGS.TSKH. Vũ Hoàng Linh, đại diện Ban Giám hiệu Trường ĐHKHTN, ĐHQGHN nhiệt liệt chúc mừng thành tích của các em học sinh đã đạt được trong kỳ thi học sinh giỏi quốc gia năm 2020 vừa qua. Hiệu trưởng đánh giá đây là nỗ lực của bản thân các em cộng với sự hỗ trợ, chăm lo của gia đình và giảng dạy, bồi dưỡng của thầy cô.</p><div style=\"box-sizing: border-box; margin: 0px; padding: 0px; text-align: center;\"><figure class=\"image\" style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238); text-align: center;\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gap-mat-hoc-sinh-thpt-chuyen-khtn-du-thi-chon-doi-tuyen-quoc-gia-thi-olympic-quoc-te-nam-2020-1.jpg\" rel=\"newsdetailslidephoto\" title=\"Gặp mặt học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của mình, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên Khoa học Tự nhiên”\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(27, 27, 27); text-decoration: none; background-color: transparent; transition-duration: 500ms; outline: none; font-size: 14px; font-weight: 400; font-family: Roboto, sans-serif; border-bottom: 1px dotted;\"><img alt=\"Gặp mặt học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của mình, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên Khoa học Tự nhiên”\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gap-mat-hoc-sinh-thpt-chuyen-khtn-du-thi-chon-doi-tuyen-quoc-gia-thi-olympic-quoc-te-nam-2020-1.jpg\" width=\"500\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; vertical-align: middle; border-style: none; height: auto !important; max-width: 100%; width: 550.5px;\"></a><figcaption style=\"box-sizing: border-box; margin: 0px; padding: 10px; display: block;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">PGS.TSKH Vũ Hoàng Linh - Hiệu trưởng Trường ĐHKHTN trao tặng Bằng khen của Giám đốc ĐHQGHN cho các bạn học sinh đạt giải Nhất trong kỳ thi Học sinh giỏi Quốc gia vừa qua.</em></figcaption></figure></div><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">Năm nay Trường THPT Chuyên KHTN có 23 học sinh ưu tú sẽ tham dự kỳ thi chọn các đội tuyển quốc gia thi Olympic quốc tế vào ngày 27 - 28/06/2020 tại các bộ môn: Toán học, Tin học, Hóa học và Sinh học.</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">&nbsp;Trước kỳ thi sắp tới, Hiệu trưởng Vũ Hoàng Linh đã gửi lời nhắn nhủ tới các bạn học sinh: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của bản thân, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên KHTN.”</p><div style=\"box-sizing: border-box; margin: 0px; padding: 0px; text-align: center;\"><figure class=\"image\" style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238); text-align: center;\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gap-mat-hoc-sinh-thpt-chuyen-khtn-du-thi-chon-doi-tuyen-quoc-te-2020.jpg\" rel=\"newsdetailslidephoto\" title=\"Gặp mặt học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của mình, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên Khoa học Tự nhiên”\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(27, 27, 27); text-decoration: none; background-color: transparent; transition-duration: 500ms; outline: none; font-size: 14px; font-weight: 400; font-family: Roboto, sans-serif; border-bottom: 1px dotted;\"><img alt=\"Gặp mặt học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của mình, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên Khoa học Tự nhiên”\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gap-mat-hoc-sinh-thpt-chuyen-khtn-du-thi-chon-doi-tuyen-quoc-te-2020.jpg\" width=\"500\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; vertical-align: middle; border-style: none; height: auto !important; max-width: 100%; width: 550.5px;\"></a><figcaption style=\"box-sizing: border-box; margin: 0px; padding: 10px; display: block;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Học sinh Ngô Quý Đăng đại diện các bạn học sinh phát biểu tại buổi gặp mặt.</em></figcaption></figure></div><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">Ngô Quý Đăng, học sinh lớp 10A1 Toán, Trường THPT Chuyên KHTN đã thay mặt cho 23 học sinh bày tỏ lòng biết ơn chân thành và gửi lời cảm ơn sâu sắc tới Nhà trường, các thầy cô và các bậc cha mẹ học sinh, những người luôn đồng hành, tạo điều kiện tốt nhất cho các em học tập.</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">Bạn Ngô Quý Đăng đã chia sẻ những cảm xúc rất chân thành và thể hiện sự quyết tâm trong kỳ thi sắp tới: “Đứng trước một kỳ thi lớn như Kỳ thi chọn học sinh giỏi THPT vào các đội tuyển quốc gia dự thi Olympic khu vực và quốc tế - chúng em phần nào cảm thấy lo lắng và hồi hộp. Tuy nhiên, với kiến thức đã được các thầy cô truyền dạy, sự tin tưởng của Nhà trường, chúng em tự tin rằng các đội tuyển của Trường ĐHKHTN sẽ phát huy được thế mạnh của mình và đạt kết quả tốt trong kỳ thi. Để không phụ niềm tin và công lao của các thầy cô, tiếp bước truyền thống tự hào của học sinh chuyên KHTN, Trường ĐHKHTN, ĐHQGHN, chúng em xin hứa quyết tâm, cố gắng hết mình để hoàn thành tốt nhất bài thi.”</p><div style=\"box-sizing: border-box; margin: 0px; padding: 0px; text-align: center;\"><figure class=\"image\" style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238); text-align: center;\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gap-mat-hoc-sinh-thpt-chuyen-khtn-du-thi-chon-doi-tuyen-quoc-gia-thi-olympic-quoc-te-nam-2020-3.jpg\" rel=\"newsdetailslidephoto\" title=\"Gặp mặt học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của mình, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên Khoa học Tự nhiên”\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(27, 27, 27); text-decoration: none; background-color: transparent; transition-duration: 500ms; outline: none; font-size: 14px; font-weight: 400; font-family: Roboto, sans-serif; border-bottom: 1px dotted;\"><img alt=\"Gặp mặt học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của mình, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên Khoa học Tự nhiên”\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gap-mat-hoc-sinh-thpt-chuyen-khtn-du-thi-chon-doi-tuyen-quoc-gia-thi-olympic-quoc-te-nam-2020-3.jpg\" width=\"500\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; vertical-align: middle; border-style: none; height: auto !important; max-width: 100%; width: 550.5px;\"></a><figcaption style=\"box-sizing: border-box; margin: 0px; padding: 10px; display: block;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Các thầy cô cùng các em học sinh xuất sắc của Trường THPT Chuyên KHTN chuẩn bị tham dự kỳ thi chụp hình lưu niệm.</em></figcaption></figure></div><div style=\"box-sizing: border-box; margin: 0px; padding: 0px; text-align: center;\"><figure class=\"image\" style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238); text-align: center;\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gap-mat-hoc-sinh-thpt-chuyen-khtn-du-thi-chon-doi-tuyen-quoc-gia-thi-olympic-quoc-te-nam-2020-2.jpg\" rel=\"newsdetailslidephoto\" title=\"Gặp mặt học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của mình, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên Khoa học Tự nhiên”\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(27, 27, 27); text-decoration: none; background-color: transparent; transition-duration: 500ms; outline: none; font-size: 14px; font-weight: 400; font-family: Roboto, sans-serif; border-bottom: 1px dotted;\"><img alt=\"Gặp mặt học sinh tham dự kỳ thi chọn đội tuyển quốc gia thi Olympic quốc tế năm 2020: “Chúc các em bình tĩnh, tự tin, thể hiện đúng năng lực của mình, góp phần nâng cao thành tích và bề dày truyền thống của Trường THPT Chuyên Khoa học Tự nhiên”\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gap-mat-hoc-sinh-thpt-chuyen-khtn-du-thi-chon-doi-tuyen-quoc-gia-thi-olympic-quoc-te-nam-2020-2.jpg\" width=\"500\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; vertical-align: middle; border-style: none; height: auto !important; max-width: 100%; width: 550.5px;\"></a><figcaption style=\"box-sizing: border-box; margin: 0px; padding: 10px; display: block;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Các quý phụ huynh cùng các em học sinh tại buổi gặp mặt học sinh tham dự kỳ thi chọn các đội tuyển quốc gia thi Olympic quốc tế năm 2020.</em></figcaption></figure></div><table style=\"box-sizing: border-box; margin: 0px; padding: 5px; border-collapse: collapse; max-width: 100%; width: 552px; border: 1px solid rgb(173, 173, 173);\"><tbody style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><tr style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><td style=\"box-sizing: border-box; margin: 0px; padding: 10px;\"><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\"><strong style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-weight: bolder;\">Danh sách 23 học sinh giỏi Trường THPT Chuyên KHTN tham dự kỳ thi chọn các đội tuyển quốc gia thi Olympics quốc tế và khu vực năm 2020</strong></p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Danh sách đội tuyển Toán học:</em></p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">1. Nguyễn Hoàng An</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">2. Phan Hữu An</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">3. Ngô Quý Đăng</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">4. Lê Xuân Hoàng</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">5. Đinh Vũ Tùng Lâm</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">6. Trương Tuấn Nghĩa</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">7. Nguyễn Đắc Quán</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Danh sách đội tuyển Hóa học:</em></p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">1. Nguyễn Hoàng Dương</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">2. Phương Hiếu Kiên</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">3. Phạm Phú Quang</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Danh sách đội tuyển Sinh học:</em></p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">1. Nguyễn Thành Đạt</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">2. Đồng Ngọc hà</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">3. Trần Đức Lộc</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">4. Lâm Hà Thanh</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">5. Hà Vũ Huyền Linh</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Danh sách đội tuyển Tin học:</em></p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">1. Vũ Minh Điểm</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">2. Nguyễn Hữu Đức</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">3. Nguyễn Minh Đức</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">4. Lê Quang Huy</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">5. Nguyễn Đình Phúc</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">6. Nguyễn Trung Thế</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">7. Vũ Hoàng Kiên</p><p style=\"box-sizing: border-box; margin: 0px 0px 1rem; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify;\">8. Bùi Hồng Đức</p></td></tr></tbody></table><p><span id=\"dnn_ctr10929_newsviewer_ctl00_lbContent\" style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"></span><span style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"></span></p><ul class=\"post-tags\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 10px 0px; display: flex; flex-wrap: wrap; border-top: 1px solid rgb(239, 239, 239); color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"></ul>', '', '', '2020-08-24 09:53:24', '2020-08-26 07:54:31', '130', '/php-intership-team/admin/SourceFile/upload/gap-mat-hoc-sinh-thpt-chuyen-khtn-du-thi-chon-doi-tuyen-quoc-gia-thi-olympic-quoc-te-nam-2020-1.jpg', ''),(131, 1, 'Trường Đại học Khoa học Tự nhiên - Đại học Quốc gia Hà Nội có 2 Phó hiệu trưởng mới', 'GS.TS. Lê Thanh Sơn, Trưởng khoa Hóa học và PGS.TS. Ngạc An Bang, Trưởng khoa Vật lý giữ chức Phó hiệu trưởng Trường Đại học Khoa học Tự nhiên từ ngày 19/6/2020.', '<p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Giám đốc Đại học Quốc gia Hà Nội vừa ký quyết định số 1766/QĐ-ĐHQGHN và 1768/QĐ-ĐHQGHN bổ nhiệm&nbsp; PGS.TS. Ngạc An Bang, Trưởng khoa Vật lý và GS.TS. Lê Thanh Sơn, Trưởng khoa Hóa học, Trường Đại học Khoa học Tự nhiên giữ chức vụ Phó hiệu trưởng Trường Đại học Khoa học Tự nhiên. Quyết định có hiệu lực kể từ ngày ký.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">GS.TS. Lê Thanh Sơn và PGS.TS. Ngạc An Bang đều là cựu sinh viên Khoa Hóa học và Khoa Vật lý, khóa 32 (1987-1992) của Trường Đại học Tổng hợp Hà Nội.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Sau khi tốt nghiệp đại học, GS.TS. Lê Thanh Sơn học cao học tại Trường, làm nghiên cứu sinh và bảo vệ thành công luận án Tiến sĩ Hóa học tại Đại học Ehime – Nhật Bản năm 1999. Năm 2007, ông được công nhận Phó giáo sư, năm 2019 ông được Hội đồng giáo sư nhà nước phong học hàm Giáo sư.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gs.ts-le-thanh-son.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường Đại học Khoa học Tự nhiên - Đại học Quốc gia Hà Nội có 2 Phó hiệu trưởng mới\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường Đại học Khoa học Tự nhiên - Đại học Quốc gia Hà Nội có 2 Phó hiệu trưởng mới\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/gs.ts-le-thanh-son.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">GS.TS. Lê Thanh Sơn.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Từ năm 1995 đến nay, GS.TS. Lê Thanh Sơn công tác tại Khoa Hóa học, Trường Đại học Khoa học Tự nhiên với các chức vụ: Giảng viên bộ môn Hóa Lý và Hóa học Dầu mỏ. Từ tháng 10/2010 ông là Phó Chủ nhiệm Khoa và từ tháng 12/2012 đến nay, ông là Trưởng khoa Hóa học. Từ năm 2017, ông là Phó Chủ tịch Hội đồng Khoa học công nghệ vật liệu và năng lượng (Đại học Quốc gia Hà Nội). Ông từng được bằng khen của Thủ tướng Chính phủ năm 2016.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">PGS.TS. Ngạc An Bang theo học ngành Vật lý chất rắn, Khoa Vật lý, Trường Đại học Tổng hợp Hà Nội, từng làm nghiên cứu viên tại Viện Vật lý và Viện Khoa học Vật liệu, Viện hàn lâm Khoa học và Công nghệ Việt Nam. Sau đó, ông tiếp tục học tập và làm nghiên cứu sinh tại khoa Vật lý, Trường Đại học Siegen, CHLB Đức. Năm 2003, ông đã bảo vệ thành công Luận án Tiến sĩ, được Đại học Siegen cấp học vị Tiến sĩ Vật lý. Năm 2018, ông được bổ nhiệm học hàm Phó giáo sư ngành Vật lý.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/dsc_8834-copy.jpg\" rel=\"newsdetailslidephoto\" title=\"Trường Đại học Khoa học Tự nhiên - Đại học Quốc gia Hà Nội có 2 Phó hiệu trưởng mới\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Trường Đại học Khoa học Tự nhiên - Đại học Quốc gia Hà Nội có 2 Phó hiệu trưởng mới\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/dsc_8834-copy.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">PGS.TS. Ngạc An Bang.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Từ năm 2003 đến nay, PGS.TS. Ngạc An Bang là giảng viên bộ môn Vật lý Đại cương, Khoa Vật lý, Trường Đại học Khoa học Tự nhiên. Từ tháng 11/2008 đến 2013, ông là Chủ nhiệm Bộ môn Vật lý Đại cương. Từ tháng 11/2012 đến nay, ông là Trưởng khoa Vật lý, Trường Đại học Khoa học Tự nhiên.</p>', '', '', '2020-08-24 09:54:52', '2020-08-26 08:10:17', '131', '/php-intership-team/admin/SourceFile/upload/ahus.jpg', ''),(132, 8, 'QS Top 50 Under 50 2021: Lần đầu tiên Đại học Quốc gia Hà Nội có mặt trong nhóm 101-150 đại học hàng đầu thế giới', 'Sáng nay, ngày 24/6/2020, Tổ chức xếp hạng đại học QS (Quacquarelli Symonds, Anh) vừa công bố kết quả cho bảng xếp hạng QS Top 50 Under 50 2021, dành cho các trường đại học có thời gian thành lập dưới 50 năm. Theo đó, Đại học Quốc gia Hà Nội (ĐHQGHN) lần đầu tiên có mặt trong bảng xếp hạng này, với vị trí trong nhóm 101-150 các trường đại học trẻ tuổi có chất lượng giáo dục hàng đầu trên thế giới. Như vậy, ĐHQGHN cùng với Đại học Quốc gia Thành phố Hồ Chí Minh trở thành 02 cơ sở giáo dục đại học duy nhất của Việt Nam lọt vào Bảng xếp hạng này', '<p><br></p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/vnu.png\" rel=\"newsdetailslidephoto\" title=\"QS Top 50 Under 50 2021: Lần đầu tiên Đại học Quốc gia Hà Nội có mặt trong nhóm 101-150 đại học hàng đầu thế giới\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"QS Top 50 Under 50 2021: Lần đầu tiên Đại học Quốc gia Hà Nội có mặt trong nhóm 101-150 đại học hàng đầu thế giới\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/vnu.png\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">ĐHQGHN lần đầu tiên có vị trí trong nhóm 101-150 trong bảng xếp hạng QS Top 50 Under 50 2021</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Tổ chức xếp hạng đại học QS công bố bảng xếp hạng QS Top 50 Under 50 lần đầu tiên vào năm 2012 nhằm vinh danh các trường đại học trẻ có chất lượng đào tạo và nghiên cứu hàng đầu thế giới. Ban đầu, Bảng xếp hạng này chỉ xếp hạng cho Top 50, tuy nhiên, kể từ năm 2015, bảng QS Top 50 Under 50 mở rộng quy mô xếp hạng cho thêm 100 trường đại học trẻ trên thế giới (Next 100 Under 50). Các trường thuộc Top 50 sẽ có thứ hạng chính xác, trong khi các trường ngoài Top 50 lần lượt được xếp hạng theo nhóm 10. Trong bảng xếp hạng năm ngoái, các trường thuộc Top 50 của bảng này đều có mặt trong Top 400 của bảng QS Thế giới.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Về phương pháp xếp hạng, bảng QS Top 50 Under 50 sử dụng các tiêu chí xếp hạng của bảng QS Thế giới. Các tiêu chí này bao gồm: (1) Đánh giá của học giả; (2) Đánh giá của nhà tuyển dụng; (3) Số trích dẫn&nbsp;khoa học/giảng viên; (4) Tỷ lệ giảng viên/sinh viên; (5) Tỷ lệ giảng viên quốc tế; (6) Tỷ lệ sinh viên quốc tế. Các tiêu chí xếp hạng này nhấn mạnh vào đóng góp và tác động của chất lượng đào tạo và nghiên cứu của trường đại học đối với xã hội (thông qua đánh giá của doanh nghiệp, học giả trong và ngoài nước) và các đóng góp cho hoạt động nghiên cứu khoa học (thông qua số trích dẫn/giảng viên). Điểm của các trường cho bảng này được lấy từ điểm tại bảng xếp hạng QS Thế giới 2021.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Về điều kiện tham gia, các cơ sở giáo dục đại học được xếp hạng trong bảng này phải được thành lập từ sau năm 1970, do đó, các trường có trên 50 tuổi sẽ không được xét. Đối với các cơ sở giáo dục được hình thành trên cơ sở sát nhập từ các cơ sở giáo dục cũ, QS sẽ công nhận thời điểm thành lập trường tại mốc thời gian sát nhập. ĐHQGHN là đại học đa ngành, đa lĩnh vực tiền thân là Đại học Đông Dương (thành lập năm 1906), tuy nhiên, đến năm 1993, ĐHQGHN được Chính phủ thành lập trên cơ sở tổ chức, sắp xếp lại 3 trường đại học ở Hà Nội: Trường Đại học Tổng hợp Hà Nội, Trường Đại học Sư phạm Hà Nội I và Trường Đại học Sư phạm Ngoại ngữ Hà Nội (theo Nghị định số 97/CP ngày 10/12/1993 của Chính phủ), do đó được xếp vào các trường đại học dưới 50 tuổi.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trong&nbsp;Bảng xếp hạng QS Top 50 Under 50 2021,&nbsp;4&nbsp;trường thuộc Top 5 cũng&nbsp;là những trường nằm trong&nbsp;Top 50 trong bảng QS Thế giới 2021, trong đó, Đại học Công nghệ Nanyang của Singapore (thành lập năm 1991) đứng vị trí số 1 (thứ 13&nbsp;Thế giới), vị trí thứ 2 thuộc về Đại học Khoa học và Công nghệ Hongkong (HKUST, thành lập năm 1991, thứ&nbsp;27&nbsp;thế giới)…&nbsp;Khu vực Đông Nam Á có 17 trường đại học có mặt trong Bảng xếp hạng này. Trong đó, Singapore có 2 trường; Malaysia có 11 trường; Brunei và Việt Nam mỗi quốc gia cùng có 02 cơ sở giáo dục đại học lọt vào Bảng xếp hạng này.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Là cơ sở giáo dục hàng đầu Việt Nam, trong những năm gần đây, ĐHQGHN đã có nhiều chủ trương mới thúc đẩy các hoạt động đổi mới giảng dạy và nghiên cứu, đổi mới sáng tạo; tham gia nhiều nhiệm vụ KHCN lớn của quốc gia và mở rộng quan hệ hợp tác với nhiều đại học uy tín trên thế giới. Uy tín học thuật và chất lượng đào tạo của ĐHQGHN đã được nhiều tổ chức xếp hạng Châu Á và thế giới ghi nhận. Mới đây nhất, tại bảng xếp hạng đại học QS Thế giới 2021 (QS World University Rankings 2021 – QS WUR 2021) công bố ngày 10/6/2020, ĐHQGHN lần thứ 3 liên tiếp đứng trong nhóm 801-1000 các trường đại học tốt nhất toàn cầu.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trước đó, ĐHQGHN lần đầu tiên có mặt trong Bảng xếp hạng các trường đại học khu vực Châu Á năm 2020 do tạp chí Times Higher Education (THE Asia) công bố ngày 4/6/2020 với vị trí trong nhóm 201 – 250, đứng đầu Việt Nam về xếp hạng tổng thể và có điểm cao nhất về các tiêu chí quan trọng là Giảng dạy, Nghiên cứu, Quốc tế hóa.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: right; font-family: Roboto, sans-serif;\">Theo VNU</p>', '', '', '2020-08-24 09:56:00', '2020-08-24 09:56:00', NULL, '/php-intership-team/admin/SourceFile/upload/anh-vnu-cover.jpg', ''),(133, 1, 'Vingroup hợp tác với 5 trường đại học, viện nghiên cứu đào tạo thạc sĩ ngành Khoa học dữ liệu', 'Ngày 18/7/2020, Quỹ Đổi mới sáng tạo VinIF (thuộc Viện Nghiên cứu Dữ liệu lớn - VinBigdata) - Tập đoàn Vingroup đã ký kết Hợp tác đào tạo thạc sĩ ngành Khoa học dữ liệu với 5 trường Đại học, Viện nghiên cứu đầu ngành của Việt Nam.', '<p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">5 đơn vị ký kết hợp tác đào tạo thạc sĩ ngành Khoa học dữ liệu với Quỹ Đổi mới sáng tạo VinIF là: Trường Đại học Khoa học Tự nhiên - Đại học Quốc Gia Hà Nội, Trường Đại học Quy Nhơn, Trường Đại học Bách khoa Hà Nội, Viện Toán học - Viện Hàn lâm Khoa học &amp; Công nghệ Việt Nam, Viện John von Neumann - Đại học Quốc gia TP. Hồ Chí Minh.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Chương trình nhằm nâng cao chất lượng đội ngũ nhân lực trẻ thông qua phát triển các chương trình đào tạo, điều kiện học tập, nghiên cứu đạt trình độ quốc tế.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Mục tiêu chương trình hướng tới xây dựng các chương trình đào thạc sĩ ngành Khoa học dữ liệu, Toán ứng dụng kết hợp lý thuyết chuyên sâu và thực tiễn tiến tới đạt trình độ quốc tế.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc_8896.jpg\" rel=\"newsdetailslidephoto\" title=\"Vingroup hợp tác với 5 trường đại học, viện nghiên cứu đào tạo thạc sĩ ngành Khoa học dữ liệu\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Vingroup hợp tác với 5 trường đại học, viện nghiên cứu đào tạo thạc sĩ ngành Khoa học dữ liệu\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc_8896.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">GS.TS. Lê Thanh Sơn, Phó Hiệu trưởng Trường Đại học Khoa học Tự nhiên (thứ 3, từ phải sang) cùng các lãnh đạo và giảng viên khoa Toán – Cơ – Tin học, Trường Đại học Khoa học Tự nhiên chụp ảnh lưu niệm với PGS.TSKH. Phan Thị Hà Dương, Giám đốc Điều hành Quỹ Đổi mới sáng tạo (áo trắng, đứng giữa).</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Theo thoả thuận hợp tác được kí kết, Quỹ VinIF sẽ hỗ trợ về nguồn lực tài chính, công nghệ, mạng lưới tri thức và chuyên gia cho mỗi cơ sở đào tạo trong 3 năm đầu để tạo tiền đề phát triển. Các hoạt động hỗ trợ bao gồm: Mời giáo sư thỉnh giảng; Xây dựng, bổ sung giáo trình và bài giảng; Tạo cơ hội học tập và nghiên cứu với các nhà khoa học uy tín trên thế giới cho học viên; Nâng cao năng lực, kinh nghiệm trong nghiên cứu khoa học. Ngoài ra, Quỹ VinIF sẽ tài trợ 10 suất học bổng cho sinh viên xuất sắc tại mỗi đơn vị nhằm khuyến khích sự phát triển của các tài năng.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Giáo sư Vũ Hà Văn – Giám đốc Khoa học Viện Nghiên cứu Dữ liệu lớn VinBDI cho biết:&nbsp;“Việt Nam đang thực sự thiếu nguồn nhân lực chất lượng cao trong lĩnh vực dữ liệu. Thông qua thỏa thuận Hợp tác đào tạo này, chúng tôi kỳ vọng sẽ đồng hành cùng các cơ sở đào tạo để khuyến khích các bạn trẻ có năng lực học tập và nghiên cứu khoa học dữ liệu – vốn là ngành không thể thiếu trong cuộc cách mạng công nghiệp 4.0”.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/vin-if-3-1595052721012.jpg\" rel=\"newsdetailslidephoto\" title=\"Vingroup hợp tác với 5 trường đại học, viện nghiên cứu đào tạo thạc sĩ ngành Khoa học dữ liệu\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Vingroup hợp tác với 5 trường đại học, viện nghiên cứu đào tạo thạc sĩ ngành Khoa học dữ liệu\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/vin-if-3-1595052721012.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Các khách mời tham dự Tọa đàm \"Nhân lực chất lượng cao Khoa học dữ liệu: Thách thức và Cơ hội\".</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trong khuôn khổ chương trình, các bên đã cùng tổ chức Tọa đàm \"Nhân lực chất lượng cao Khoa học dữ liệu: Thách thức và Cơ hội\". Người tham dự có cơ hội tiếp cận góc nhìn mới về Khoa học dữ liệu và những thách thức căn bản của hoạt động đào tạo, phát triển nhân lực chất lượng cao trong lĩnh vực này.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">GS.TS. Lê Thanh Sơn,&nbsp; Phó Hiệu trưởng Trường Đại học Khoa học Tự nhiên cho biết: “Trường Đại học Khoa học Tự nhiên là một trong những đơn vị đầu tiên trong cả nước mở ngành Thạc sĩ Khoa học dữ liệu, đến nay đã tuyển sinh được 3 khóa. Năm 2020, lần đầu tiên Trường tuyển sinh đại học chính quy ngành Khoa học dữ liệu, tiến tới đào tạo tiến sĩ ngành Khoa học dữ liệu. Điều này cho thấy Nhà trường đã nắm bắt được xu thế, nhu cầu của thời đại đối với nguồn nhân lực chất lượng cao này”.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc_8313.jpg\" rel=\"newsdetailslidephoto\" title=\"Vingroup hợp tác với 5 trường đại học, viện nghiên cứu đào tạo thạc sĩ ngành Khoa học dữ liệu\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Vingroup hợp tác với 5 trường đại học, viện nghiên cứu đào tạo thạc sĩ ngành Khoa học dữ liệu\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/dsc_8313.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 550.5px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">GS.TS. Lê Thanh Sơn, Phó Hiệu trưởng Trường Đại học Khoa học Tự nhiên phát biểu tại Tọa đàm.</figcaption></figure></div><table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"margin: 0px; padding: 5px; max-width: 100%; width: 552px; border: 1px solid rgb(173, 173, 173); color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 10px;\"><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">Quỹ Đổi mới sáng tạo VinIF (thuộc Viện Nghiên cứu Dữ liệu lớn – VinBigdata) do tập đoàn Vingroup thành lập với vai trò hỗ trợ các tổ chức, cá nhân thực hiện nghiên cứu khoa học, công nghệ và đổi mới sáng tạo, nhằm tạo ra những thay đổi tích cực và bền vững cho tương lai ngành khoa học công nghệ Việt Nam.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">Quỹ VinIF triển khai 04 chương trình trọng tâm, gồm: Hỗ trợ các dự án nghiên cứu; Tài trợ học bổng sau đại học; Hợp tác đào tạo thạc sĩ và Hợp tác, Tài trợ hội nghị, hội thảo. Trước đó, trong năm 2019, Quỹ VinIF tài trợ 124 tỷ đồng cho 158 học viên cao học, nghiên cứu sinh và 20 dự án nghiên cứu khoa học công nghệ tại Việt Nam.</p></td></tr></tbody></table><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">&nbsp;</p><table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"margin: 0px; padding: 5px; max-width: 100%; width: 552px; border: 1px solid rgb(173, 173, 173); color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"><tbody style=\"margin: 0px; padding: 0px;\"><tr style=\"margin: 0px; padding: 0px;\"><td style=\"margin: 0px; padding: 10px;\"><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">Khoa học dữ liệu dựa trên ba nền tảng kiến thức: Toán học, Thống kê và Tin học; phân tích và khám phá tri thức mới từ các dữ liệu thực tế, tồn tại dưới nhiều dạng cấu trúc khác nhau như số liệu, văn bản, hình ảnh, âm thanh. Tại Việt Nam, không nhiều các đơn vị đào tạo có đủ quy mô về nhân sự, đáp ứng điều kiện đủ để xây dựng các chương trình đào tạo thạc sĩ ngành Khoa học dữ liệu.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">Trường ĐH Khoa học Tự nhiên, ĐHQG Hà Nội là đơn vị triển khai chương trình đào tạo Thạc sĩ chuyên ngành Khoa học dữ liệu đầu tiên trên cả nước từ năm học 2018-2019.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); line-height: 1.5em; text-align: justify;\">Đến nay, sau 3 khóa tuyển sinh, chương trình đã nhận được những phản hồi rất tích cực từ học viên, khẳng định vai trò và tính hấp dẫn đặc biệt của một chương trình đào tạo liên ngành đáp ứng nhu cầu nhân lực cao: từ những lĩnh vực có nhu cầu phân tích dữ liệu khoa học như y sinh học, môi trường, địa lí, địa chất, khí tượng, thuỷ văn, nông nghiệp, xã hội học, … đến những lĩnh vực có nhu cầu phân tích dữ liệu trong nền kinh tế số như tài chính, kinh doanh thương mại, chăm sóc khách hàng, mạng xã hội,..</p></td></tr></tbody></table>', '', '', '2020-08-24 09:58:43', '2020-08-25 01:17:17', NULL, '/php-intership-team/admin/SourceFile/upload/vin-if-3-1595052721012.jpg', ''),(134, 5, 'Một số kết quả nổi bật của Đảng bộ Trường ĐHKHTN lần thứ XXVI, nhiệm kỳ 2015 - 2020', '“Tập trung mọi nguồn lực, trí tuệ, phấn đấu xây dựng Trường ĐHKHTN trở thành trường đại học nghiên cứu tiên tiến, là trung tâm đào tạo nhân tài, nguồn nhân lực chất lượng cao, đứng đầu trong hệ thống giáo dục Việt Nam về lĩnh vực khoa học cơ bản, góp phần tích cực vào sự nghiệp đổi mới giáo dục đào tạo và công cuộc công nghiệp hóa, hiện đại hóa đất nước” - Đó là mục tiêu chiến lược đã được xác định tại Đại hội Đảng bộ Trường Đại học Khoa học Tự nhiên, Đại học Quốc gia Hà Nội lần thứ XXVI (được tổ chức vào ngày 14, 15 tháng 5 năm 2015).', '<p><br></p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/dai-hoi-26.jpg\" rel=\"newsdetailslidephoto\" title=\"Một số kết quả nổi bật của Đảng bộ Trường ĐHKHTN lần thứ XXVI, nhiệm kỳ 2015 - 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Một số kết quả nổi bật của Đảng bộ Trường ĐHKHTN lần thứ XXVI, nhiệm kỳ 2015 - 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/dai-hoi-26.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Đại hội Đảng bộ Trường Đại học Khoa học Tự nhiên lần thứ XXVI</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Để triển khai thực hiện Nghị quyết Đại hội đại biểu Đảng bộ Trường lần thứ XXVI, Nhà trường đã ban hành Kế hoạch triển khai thực hiện các nhiệm vụ trọng tâm. Trong nhiệm kỳ 2015-2020, Đảng ủy đã chỉ đạo chính quyền triển khai có hiệu quả các nhiệm vụ chính trị và đạt được nhiều kết quả quan trọng.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Duy trì qui mô đào tạo ổn định, chất lượng đào tạo được chú trọng hàng đầu</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trong nhiệm kỳ 2015-2020, Đảng ủy Trường Đại học Khoa học Tự nhiên (ĐHKHTN) đã bám sát Nghị quyết của Đảng ủy Đại học Quốc gia Hà Nội (ĐHQGHN) về các yếu tố nâng cao chất lượng đào tạo theo tinh thần Nghị quyết số 29-NQ/TW, ngày 04/11/2013 Hội nghị lần thứ tám Ban Chấp hành Trung ương Đảng (khóa XI) về đổi mới căn bản và toàn diện giáo dục và đào tạo để chỉ đạo chính quyền và các đoàn thể. Theo chủ trương của Đảng ủy, qui mô đào tạo đại học chính quy và THPT chuyên được duy trì khá ổn định trong giai đoạn 2015-2020. Chỉ tiêu tuyển sinh đại học chính qui ổn định ở mức 1450-1500 sinh viên/năm.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/sv-khoa-hoa-nghien-cuu.jpg\" rel=\"newsdetailslidephoto\" title=\"Một số kết quả nổi bật của Đảng bộ Trường ĐHKHTN lần thứ XXVI, nhiệm kỳ 2015 - 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Một số kết quả nổi bật của Đảng bộ Trường ĐHKHTN lần thứ XXVI, nhiệm kỳ 2015 - 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/sv-khoa-hoa-nghien-cuu.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Sinh viên khoa Hóa học nghiên cứu khoa học tại phòng thí nghiệm của Khoa</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Một số ngành đào tạo bậc đại học thuộc khối khoa học cơ bản khó tuyển sinh đã được tái sắp xếp vào các ngành khác. Bên cạnh đó, Trường cùng các Khoa chủ động xây dựng các đề án mở mới các chương trình đào tạo có nhu cầu xã hội cao, theo kịp các yêu cầu mới của cuộc cách mạng công nghiệp 4.0 như: Khoa học thông tin địa không gian (2018); Tài nguyên và môi trường nước (2019).</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><span style=\"color: rgb(67, 67, 67); text-align: justify;\">hí được sử dụng hợp lý, hiệu quả, đúng quy định của Nhà nước và đã đạt được mục tiêu đề ra về thu nhập tăng thêm của cán bộ năm sau cao hơn năm trước 10%. Bên cạnh đó, một số&nbsp;chính sách nhằm gia tăng nguồn lực tài chính đã thu được những hiệu quả&nbsp;nhất định.</span><br></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Dưới sự quan tâm chỉ đạo sát sao của Đảng ủy&nbsp;Nhà trường và Ban giám đốc ĐHQGHN, cơ sở vật chất của trường đã được tăng cường một cách đáng kể. Cụ thể, thông qua các dự án đầu tư chiều sâu, dự án tăng cường năng lực có nguồn vốn ngân sách nhà nước, các trang thiết bị&nbsp;nghiên cứu khoa học thuộc các đơn vị&nbsp;được bổ&nbsp;sung, nâng cấp kịp thời; các trang thiết bị&nbsp;được đầu tư mới đều đạt tiêu chuẩn khu vực và thế&nbsp;giới, góp phần đáng kể vào sự gia tăng số&nbsp;lượng, chất lượng công bố&nbsp;quốc tế&nbsp;của&nbsp;các nhà khoa học trong toàn trường.</p><div style=\"margin: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px; text-align: center;\"><figure class=\"image\" style=\"padding: 0px; display: inline-block; background: rgb(251, 251, 251); border: 1px solid rgb(238, 238, 238);\"><a class=\"newsslidephoto\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/khuon-vien-truong.jpg\" rel=\"newsdetailslidephoto\" title=\"Một số kết quả nổi bật của Đảng bộ Trường ĐHKHTN lần thứ XXVI, nhiệm kỳ 2015 - 2020\" style=\"margin: 0px; padding: 0px; color: rgb(27, 27, 27); transition-duration: 500ms; outline: none; border-bottom: 1px dotted;\"><img alt=\"Một số kết quả nổi bật của Đảng bộ Trường ĐHKHTN lần thứ XXVI, nhiệm kỳ 2015 - 2020\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/06/khuon-vien-truong.jpg\" width=\"500\" style=\"margin: 0px; padding: 0px; max-width: 100%; width: 724.656px; height: auto !important;\"></a><figcaption style=\"margin: 0px; padding: 10px;\">Khuôn viên trường nhiều cây xanh, là nơi lý tưởng cho các bạn sinh viên nghỉ ngơi sau giờ học.</figcaption></figure></div><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trước nhu cầu cấp bách về diện tích mặt bằng của một cơ sở giáo dục đại học nghiên cứu hàng đầu của cả&nbsp;nước, lãnh đạo ĐHQGHN đã lựa chọn Trường ĐHKHTN là đơn vị&nbsp;đầu tiên được được quy hoạch chi tiết, đầu tư xây dựng trên diện tích 65,1 ha tại&nbsp;khu Hòa Lạc (dự án QG-HN07). Một số&nbsp;bước tiến quan trọng giai đoạn 2015-2020 đã được Nhà trường thực hiện, bao gồm:</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">10 ngành đào tạo đại học được công nhận đạt chuẩn chất lượng giáo dục cấp chương trình đào tạo theo Bộ tiêu chuẩn của Mạng lưới các trường đại học Đông Nam Á (AUN-QA) và 01 ngành đã thực hiện đánh giá, đang đợi&nbsp;kết quả.&nbsp;<br></p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">- Trong bảng xếp hạng QS World University Rankings 2020, Trường ĐHKHTN đã đóng góp không nhỏ trong sự thành công của ĐHQGHN với xếp hạng đại học thuộc nhóm 801-1000 thế giới; lĩnh vực Toán học lần đầu tiên được xếp hạng với vị trí trong nhóm 401 - 450 thế giới, số 1 ở Việt Nam; lĩnh vực Vật lý và Thiên văn học được xếp trong nhóm 551 - 600 thế giới, số 1 ở Việt Nam; lĩnh vực Khoa học máy tính và Hệ thống thông tin được xếp trong nhóm 501 - 550 thế giới, thứ 2 ở Việt Nam. Mới đây nhất, trong bảng xếp hạng QS Top 50 Under 50 2021 dành cho các trường đại học có thời gian thành lập dưới 50 năm, Đại học Quốc gia Hà Nội (ĐHQGHN) lần đầu tiên có mặt với vị trí trong nhóm 101-150 các trường đại học trẻ tuổi có chất lượng giáo dục hàng đầu trên thế giới.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(67, 67, 67); font-size: 14px; line-height: 1.5em; text-align: justify; font-family: Roboto, sans-serif;\">Trong nhiệm kỳ 2015-2020, Đảng bộ Trường ĐHKHTN đã lãnh đạo, chỉ đạo toàn diện mọi mặt hoạt động của Nhà trường. Các đảng viên của Đảng bộ đã nêu cao tính tiên phong, trách nhiệm của người đảng viên trong việc thực hiện các chủ trương, Nghị quyết của Đảng các cấp. Bên cạnh đó, sự đoàn kết, đồng thuận cao, tinh thần quyết tâm, nỗ lực phấn đấu và có trách nhiệm cao của toàn Đảng bộ trong việc thực hiện các nhiệm vụ chính trị của Nhà trường là yếu tố quan trọng để Nhà trường vượt qua nhiều khó khăn thử thách, hoàn thành thắng lợi Nghị quyết Đại hội Đảng bộ Trường lần thứ XXVI.</p>', '', '', '2020-08-24 09:59:53', '2020-08-24 09:59:53', NULL, '/php-intership-team/admin/SourceFile/upload/dai-hoi-26.jpg', ''),(135, 2, 'Nhập học lớp 10 THPT Chuyên KHTN', '<dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Thời gian</dt>\r\n                        <dd style=\"display: block !important;font-size: 14px;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: 08:00 01/08/2020</dd>\r\n\r\n                        <dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Địa điểm</dt>\r\n                        <dd style=\"display: block !important;font-size: 14px;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: Nhà A-B, Trường THPT Chuyên Khoa học Tự nhiên, số 182 Lương Thế Vinh, Thanh Xuân, Hà Nội</dd>\r\n                        <dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Đại biểu tham dự</dt>\r\n                        <dd style=\"display: block !important;font-size: 14px;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: </dd>', '<h3 style=\"margin-right: 0px; margin-left: 0px; padding: 0px; font-family: Roboto, sans-serif; line-height: 1.3; color: rgb(0, 0, 0);\">Nội dung:</h3><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">Sau khi chuẩn bị hồ sơ theo hướng dẫn tại địa chỉ <a href=\"http://tuyensinh.hus.vnu.edu.vn/thpt-chuyen.html\" style=\"margin: 0px; padding: 0px; color: rgb(141, 141, 141); transition-duration: 500ms; outline: none;\">http://tuyensinh.hus.vnu.edu.vn/thpt-chuyen.html</a> (mục Hướng dẫn thủ tục nhập học); phụ huynh (học sinh không phải đến khi nhập học) mang hồ sơ đến làm thủ tục nhập  học cho các bạn học sinh đã trúng tuyển theo thời gian và địa điểm như sau :</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">* Thời gian: Thứ bảy, ngày 01 tháng 8 năm 2020</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">* Địa điểm: nhà A-B, Trường THPT Chuyên Khoa học Tự nhiên, số 182 Lương Thế Vinh, Thanh Xuân, Hà Nội</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">I. KẾ HOẠCH ĐÓN NHẬN HỌC SINH VÀ THU HỒ SƠ:</span></p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">1. Sáng ngày 01/8/2020:</span></em></p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">*Từ 8h00’- 9h30’:  Học sinh có tên từ A đến L( ví dụ: An, Anh,...Đức...Linh)</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">*Từ 9h30’- 11h30’:  Học sinh có tên từ M đến Y( ví dụ: Mai, Nam,...... Yến)</p><ul style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"><li style=\"margin: 0px; padding: 0px; list-style: none;\">Lớp chuyên Sinh học:  nộp hồ sơ và học phí tại Giảng đường 201; Giảng đường 202</li><li style=\"margin: 0px; padding: 0px; list-style: none;\">Lớp chuyên Toán học: nộp hồ sơ và học phí tại Giảng đường 203; Giảng đường 204</li><li style=\"margin: 0px; padding: 0px; list-style: none;\">Lớp chuyên Tin học: nộp hồ sơ và học phí tại Giảng đường 205; Giảng đường 206</li></ul><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">2. Chiều ngày 01/8/2020:</span></em> Từ 13h30’-17h00’</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">*Từ 13h30’- 15h00’:  Học sinh có tên từ A đến L( ví dụ: An, Anh,...Đức...Linh)</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">*Từ 15h00’- 16h30’:  Học sinh có tên từ M đến Y( ví dụ: Mai, Nam,...... Yến)</p><ul style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"><li style=\"margin: 0px; padding: 0px; list-style: none;\">Lớp Chất lượng cao: nộp hồ sơ và học phí tại Giảng đường 201; Giảng đường 202</li><li style=\"margin: 0px; padding: 0px; list-style: none;\">Lớp chuyên Vật lý học: nộp hồ sơ và học phí tại Giảng đường 203; Giảng đường 204</li><li style=\"margin: 0px; padding: 0px; list-style: none;\">Lớp chuyên Hóa học: nộp hồ sơ và học phí tại Giảng đường 205; Giảng đường 206</li></ul><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">3. Bộ phận giải quyết nội trú và đăng ký tạm trú tại Giảng đường 101</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">4. Ban chỉ đạo và giải quyết chính sách tại Văn phòng  (tầng 1)</p><hr style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"><h3 style=\"margin-right: 0px; margin-left: 0px; padding: 0px; font-family: Roboto, sans-serif; line-height: 1.3; color: rgb(0, 0, 0);\">Liên hệ</h3><dl class=\"details\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 14px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><dt style=\"margin: 0px; padding: 0px; float: left;\">Họ và tên</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd><dt style=\"margin: 0px; padding: 0px; float: left;\">E-Mail</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd><dt style=\"margin: 0px; padding: 0px; float: left;\">Điện thoại</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd><dt style=\"margin: 0px; padding: 0px; float: left;\">Địa chỉ liên hệ</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd></dl>', '08/01/2020 08:00 AM ', ' 08/01/2020 06:00 PM', '2020-08-24 10:00:47', '2020-08-24 15:55:26', NULL, '/php-intership-team/admin/SourceFile/upload/nhap-hoc-lop-10-thpt-chuyen-khtn-1521-0.jfif', 'Nhà A-B, Trường THPT Chuyên Khoa học Tự nhiên, số 182 Lương Thế Vinh, Thanh Xuân, Hà Nội'),(136, 2, 'Kiểm tra và phỏng vấn vào lớp 10 Chất lượng cao', ' <dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Thời gian</dt>\r\n                        <dd style=\"display: block !important;font-size: 14px;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: 08:00 31/07/2020</dd>\r\n\r\n                        <dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Địa điểm</dt>\r\n                        <dd style=\"display: block !important;font-size: 14px;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: Trường THPT Chuyên KHTN, 182 Lương Thế Vinh, Phường Thanh Xuân Bắc, Quận Thanh Xuân, Hà Nội</dd>\r\n                        <dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Đại biểu tham dự</dt>\r\n                        <dd style=\"display: block !important;font-size: 14px;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: </dd>', '<h3 style=\"margin-right: 0px; margin-left: 0px; padding: 0px; font-family: Roboto, sans-serif; line-height: 1.3; color: rgb(0, 0, 0);\">Nội dung:</h3><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: rgb(255, 0, 0);\"><span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">LƯU Ý THÍ SINH</span></span></p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: rgb(255, 0, 0);\"><span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">KIỂM TRA VÀ PHỎNG VẤN VÀO LỚP 10 CLC</span></span></p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif; text-align: justify;\">Hội đồng tuyển sinh lớp 10 THPT Chuyên KHTN mời các thí sinh đã nộp đơn đăng ký xét tuyển vào lớp 10 chất lượng cao, Trường THPT Chuyên KHTN năm 2020 tới dự kiểm tra và phỏng vấn theo đúng lịch đã thông báo:</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif; text-align: justify;\">- <span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">Thời gian:</span> Thứ sáu, 31/7/2020 (bắt đầu từ 08h00).</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif; text-align: justify;\">- <span style=\"margin: 0px; padding: 0px; font-weight: bolder;\">Địa điểm:</span> Trường THPT Chuyên KHTN, 182 Lương Thế Vinh, Phường Thanh Xuân Bắc, Quận Thanh Xuân, Hà Nội.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif; text-align: justify;\">1. Thí sinh có mặt vào 07h30’ để xem số báo danh và phòng thi;</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif; text-align: justify;\">2. Mang theo thẻ dự thi (thẻ mà thí sinh đã nhận khi thi vào các lớp chuyên). Trường hợp bị mất thẻ, thí sinh phải mang theo Chứng minh thư nhân dân hoặc Căn cước công dân mới được vào phòng thi.</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif; text-align: justify;\">3. Tuân thủ việc phòng, chống dịch bệnh COVID-19 theo sự hướng dẫn của các cơ quan chuyên môn; chủ động thực hiện các hoạt động chăm sóc sức khỏe bản thân để phòng chống dịch bệnh.</p><hr style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"><h3 style=\"margin-right: 0px; margin-left: 0px; padding: 0px; font-family: Roboto, sans-serif; line-height: 1.3; color: rgb(0, 0, 0);\">Liên hệ</h3><dl class=\"details\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 14px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><dt style=\"margin: 0px; padding: 0px; float: left;\">Họ và tên</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd><dt style=\"margin: 0px; padding: 0px; float: left;\">E-Mail</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd><dt style=\"margin: 0px; padding: 0px; float: left;\">Điện thoại</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd><dt style=\"margin: 0px; padding: 0px; float: left;\">Địa chỉ liên hệ</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd></dl>', '07/31/2020 08:00 AM ', ' 07/31/2020 06:00 PM', '2020-08-24 10:01:36', '2020-08-24 15:53:52', NULL, '/php-intership-team/admin/SourceFile/upload/kiem-tra-va-phong-van-vao-lop-10-chat-luong-cao-1521-1.jfif', 'Trường THPT Chuyên KHTN, 182 Lương Thế Vinh, Phường Thanh Xuân Bắc, Quận Thanh Xuân, Hà Nội'),(137, 2, 'Lễ ký kết hợp tác đào tạo Thạc sĩ Khoa học Dữ liệu & Tọa đàm Nhân lực chất lượng cao Khoa học Dữ liệu', '<dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Thời gian</dt>\r\n                        <dd style=\"display: block !important;font-size: 14px;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: 00:00 18/07/2020</dd>\r\n\r\n                        <dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Địa điểm</dt>\r\n                        <dd style=\"display: block !important;font-size: 14px;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: Phòng hội thảo tầng 10, Thư viện Tạ Quang Bửu, Trường Đại học Bách Khoa Hà Nội, (cổng đường Trần Đại Nghĩa), Số 1 Đại Cồ Việt, Hai Bà Trưng, Hà Nội</dd>\r\n                        <dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Đại biểu tham dự</dt>\r\n                        <dd style=\"display: block !important;font-size: 14px;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: Tọa đàm có sự tham dự của các diễn giả: - PGS.TS. Đỗ Ngọc Mỹ, Hiệu trưởng Trường Đại học Quy Nhơn - PGS.TS. Hoàng Minh Sơn, Hiệu trưởng Trường Đại học Bách Khoa Hà Nội - GS.TSKH. Phùng Hồ Hải, Viện trưởng Viện Toán học - PGS.TS. Trần Minh Triết, Viện trưởng Viện John von Neumann - PGS.TSKH. Vũ Hoàng Linh, Hiệu trưởng Trường Đại học Khoa học Tự nhiên - GS. Vũ Hà Văn, Giám đốc khoa học Viện Nghiên cứu Dữ liệu lớn - GS. Đại học Yale - PGS.TSKH. Phan Thị Hà Dương, Giám đốc Điều hành Quỹ Đổi mới sáng tạo - PGS. Viện Toán học</dd>', '<h3 style=\"margin-right: 0px; margin-left: 0px; padding: 0px; font-family: Roboto, sans-serif; line-height: 1.3; color: rgb(0, 0, 0);\">Nội dung:</h3><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\"><a data-caption=\"Lễ ký kết hợp tác đào tạo Thạc sĩ Khoa học Dữ liệu  Tọa đàm Nhân lực chất lượng cao Khoa học Dữ liệu\" data-fancybox=\"anhtrongbaiviet\" href=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/le-ky-ket-hop-tac-dao-tao-thac-si-khoa-hoc-du-lieu-1521-0.jpg\" title=\"Lễ ký kết hợp tác đào tạo Thạc sĩ Khoa học Dữ liệu  Tọa đàm Nhân lực chất lượng cao Khoa học Dữ liệu\" style=\"margin: 0px; padding: 0px; color: rgb(141, 141, 141); transition-duration: 500ms; outline: none;\"><img alt=\"Lễ ký kết hợp tác đào tạo Thạc sĩ Khoa học Dữ liệu  Tọa đàm Nhân lực chất lượng cao Khoa học Dữ liệu\" src=\"http://hus.vnu.edu.vn/DATA/IMAGES/2020/07/le-ky-ket-hop-tac-dao-tao-thac-si-khoa-hoc-du-lieu-1521-0.jpg\" style=\"margin: 0px; padding: 0px; height: auto; max-width: 100%;\"></a>Thế giới đang vận động với một lượng dữ liệu khổng lồ, với Trí tuệ nhân tạo, Học máy, Học sâu, Điện toán đám mây… ai làm chủ dữ liệu sẽ là người làm chủ tương lai. Bối cảnh này đặt ra một nhu cầu cấp thiết về nguồn nhân lực chất lượng cao có đủ khả năng và kĩ năng nghiên cứu, tính toán cần thiết để thực hiện các phân tích trên dữ liệu có quy mô lớn. Tuy nhiên ở Việt Nam hiện nay, mới có một số ít cơ sở đào tạo triển khai chương trình đào tạo sau đại học định hướng khoa học dữ liệu.<br style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\">Nằm trong chiến lược thay đổi cách thức nghiên cứu khoa học công nghệ và tư duy đào tạo, phát triển của thế hệ khoa học tương lai tại Việt Nam, Lễ ký kết Hợp tác đào tạo thạc sĩ Khoa học dữ liệu được Quỹ Đổi mới sáng tạo Vingroup - Viện Nghiên cứu Dữ liệu lớn VinBigdata tổ chức để triển khai hợp tác với 05 trường đại học, viện nghiên cứu, bao gồm: Trường Đại học Bách khoa Hà Nội, Trường Đại học Khoa học Tự nhiên, Đại học Quốc Gia Hà Nội, Trường Đại học Quy Nhơn, Viện Toán học và Viện John von Neumann, Đại học Quốc gia TP.HCM nhằm hỗ trợ nguồn lực tài chính, công nghệ, mạng lưới tri thức và chuyên gia cho các cơ sở đào tạo và học viên cao học có điều kiện học tập, nghiên cứu đạt tầm quốc tế.<br style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\">Trong khuôn khổ chương trình, Tọa đàm \"Nhân lực chất lượng cao Khoa học dữ liệu: Thách thức và Cơ hội\" sẽ mang đến cho người tham dự góc nhìn mới về khoa học dữ liệu và những thách thức căn bản của hoạt động đào tạo, phát triển nhân lực chất lượng cao trong lĩnh vực này. Viện nghiên cứu Dữ liệu lớn, Quỹ Đổi mới sáng tạo hy vọng sẽ có thể đề xuất một số cơ hội hợp tác mới với các trường đai học và Viện nghiên cứu trong cả nước.<br style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\">Tọa đàm có sự tham dự của các diễn giả:<br style=\"margin: 0px; padding: 0px;\">- PGS.TS. Đỗ Ngọc Mỹ, Hiệu trưởng Trường Đại học Quy Nhơn<br style=\"margin: 0px; padding: 0px;\">- PGS.TS. Hoàng Minh Sơn, Hiệu trưởng Trường Đại học Bách Khoa Hà Nội<br style=\"margin: 0px; padding: 0px;\">- GS.TSKH. Phùng Hồ Hải, Viện trưởng Viện Toán học<br style=\"margin: 0px; padding: 0px;\">- PGS.TS. Trần Minh Triết, Viện trưởng Viện John von Neumann<br style=\"margin: 0px; padding: 0px;\">- PGS.TSKH. Vũ Hoàng Linh, Hiệu trưởng Trường Đại học Khoa học Tự nhiên<br style=\"margin: 0px; padding: 0px;\">- GS. Vũ Hà Văn, Giám đốc khoa học Viện Nghiên cứu Dữ liệu lớn - GS. Đại học Yale<br style=\"margin: 0px; padding: 0px;\">- PGS.TSKH. Phan Thị Hà Dương, Giám đốc Điều hành Quỹ Đổi mới sáng tạo - PGS. Viện Toán học</p><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-size: 14px; line-height: 1.4; font-family: Roboto, sans-serif;\">', '07/18/2020 08:00 AM ', ' 07/18/2020 02:00 PM', '2020-08-24 10:02:14', '2020-08-24 15:51:30', NULL, '/php-intership-team/admin/SourceFile/upload/le-ky-ket-hop-tac-dao-tao-thac-si-khoa-hoc-du-lieu-1521-0.jpg', 'Phòng hội thảo tầng 10, Thư viện Tạ Quang Bửu, Trường Đại học Bách Khoa Hà Nội, (cổng đường Trần Đại Nghĩa), Số 1 Đại Cồ Việt, Hai Bà Trưng, Hà Nội'),(138, 2, 'Hoa khôi và Nam vương Trường Đại học Khoa học Tự nhiên năm 2019', '<dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Thời gian</dt>\r\n                        <dd style=\"display: block !important;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: 00:00 20/06/2019</dd>\r\n\r\n                        <dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Địa điểm</dt>\r\n                        <dd style=\"display: block !important;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: Trường Đại học Khoa học Tự nhiên</dd>\r\n                        <dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Đại biểu tham dự</dt>\r\n                        <dd style=\"display: block !important;\r\n                        margin: 15px 0 15px 100px !important;\r\n                        text-align: justify !important;\">: </dd>', '<h3 style=\"margin-right: 0px; margin-left: 0px; padding: 0px; font-family: Roboto, sans-serif; line-height: 1.3; color: rgb(0, 0, 0);\">Nội dung:</h3><p><span style=\"color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\">Nỗ lực hết mình vượt qua vòng sơ loại và 5 phần thi phụ, top 20 nam thanh nữ tú của Trường Đại học Khoa học Tự nhiên (Trường) đã tỏa sáng trong đêm Chung kết Miss&amp;Mr HUS 2019 đáng nhớ tại sân khấu ngoài trời được tổ chức tại khuôn viên 334 Nguyễn Trãi vào ngày 22/4/2019. Ngôi vị cao nhất là Hoa khôi và Nam vương lần lượt thuộc về nữ sinh Nguyễn Thị Mai Hương, Khoa Địa lý và nam sinh Nguyễn Hữu Tài, Khoa Vật lý.</span></p><hr style=\"margin-right: 0px; margin-left: 0px; padding: 0px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif; font-size: 14px;\"><h3 style=\"margin-right: 0px; margin-left: 0px; padding: 0px; font-family: Roboto, sans-serif; line-height: 1.3; color: rgb(0, 0, 0);\">Liên hệ</h3><dl class=\"details\" style=\"margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; font-size: 14px; color: rgb(33, 37, 41); font-family: Roboto, sans-serif;\"><dt style=\"margin: 0px; padding: 0px; float: left;\">Họ và tên</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd><dt style=\"margin: 0px; padding: 0px; float: left;\">E-Mail</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd><dt style=\"margin: 0px; padding: 0px; float: left;\">Điện thoại</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd><dt style=\"margin: 0px; padding: 0px; float: left;\">Địa chỉ liên hệ</dt><dd style=\"margin: 15px 0px 15px 100px; padding: 0px; text-align: justify;\">:</dd></dl>', '06/20/2019 12:00 AM ', ' 06/21/2019 12:00 AM', '2020-08-24 10:02:31', '2020-08-24 10:02:31', NULL, '/php-intership-team/admin/SourceFile/upload/namvuong.jpg', 'Trường Đại học Khoa học Tự nhiên'),(139, 2, 'Hội thảo định hướng nghề nghiệp và ngày hội phỏng vấn việc làm', '<dt style=\"margin: 0;\r\n                            padding: 0;\r\n                            font-size: 14px;font-weight: bold;\r\n                            float: left;\">Thời gian