INSERT INTO `adminHUS`.`topic_project` (`id`,`id_project`,`name`,`lead_researcher`,`workplace`,`approval_date`,`acceptance_date`,`result`,`deleted_at`,`id_type`) VALUES (1, 'KC.04.09/11-15', 'Nghiên cứu chế tạo chế phẩm probiotic dạng bào tử Bacillus subtilis tái tổ hợp để phòng virus gây bệnh đốm trắng ở tôm.', 'Phan Tuấn Nghĩa', 'Phòng thí nghiệm trọng điểm KLEPT', '2015-11-11', '2015-11-11', 'Xuất sắc', '1', 1),(4, 'ĐT-PTNTĐ.2011-G/04', 'Nghiên cứu tạo protein thụ thể tái tổ hợp để xây dựng mô hình sàng lọc dược phẩm.', 'Đinh Đoàn Long', 'Khoa Sinh học', '2015-08-11', '2015-08-11', 'Khá', '4', 1),(5, '101.02.65.09', 'Giải tích số trong phương trình vi phân và ứng dụng', 'Nguyễn Hữu Công', 'Khoa Toán - Cơ - Tin học', '2012-01-01', '2012-01-01', 'Đạt', '5', 2),(6, 'QG.09.02	', 'Nghiên cứu ảnh hưởng của sự giam cầm điện tử và phonon lên các hiệu ứng cao tần gây bởi trường sóng điện từ trong các hệ bán dẫn một chiều và hai chiều (siêu mạng, hố lượng tử, dây lượng tử)	', 'Nguyễn Quang Báu	', 'Khoa Vật lý	', '2011-04-15', '2011-04-15', 'Tốt\r\n', '6', 4),(7, 'TN.10.19	', 'Điều chế và tính chất của 5-(axetamidophenyl)-1,3,4-oxadiazol-2-thiol	', 'Nguyễn Thị Sơn	', 'Khoa Hóa học	', '2011-06-22', '2011-06-22', 'Tốt', NULL, 3),(8, 'QG.09.08	', 'Nghiên cứu chế tạo hệ xúc tác nano Pt / vật liệu mao quản trung bình chứa silic để oxi hoá glucozơ chọn lọc thành axit gluconic ứng dụng làm thực phẩm chức năng.	', 'Trần Thị Như Mai	', 'Khoa Hóa học	', '2011-04-27', '2011-04-27', 'Tốt\r\n', NULL, 4),(9, '105.09.51.09', 'Nghiên cứu cơ sở khoa học và cơ sở pháp lý đánh giá ô nhiễm môi trường không khí phục vụ phát triển bền vững giải quyết tranh chấp môi trường có liên quan', 'Hoàng Xuân Cơ', 'Khoa Môi trường', '2016-08-04', '2016-08-04', 'Không đạt', NULL, 2),(10, 'TN.10.18', 'Nghiên cứu khai thác thư viện tính toán cho FORTRAN	', 'Nguyễn Đức Vinh	', 'Khoa Vật lý	', '2012-08-17', '2012-08-17', 'Khá\r\n', NULL, 3),(11, 'TN.10.17	', 'Thử nghiệm phương pháp địa chấn trong khảo sát công 